import React from "react";
import styled from "styled-components";
import cover from "../../Assets/selfparking.jpg";

const HeadMain = () => {
    return (
        <HeadContainer>
            <CoverImg src={cover} alt="Capa do blog" />
        </HeadContainer>
    );
};

const HeadContainer = styled.section`
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 20px 0;
`;

const CoverImg = styled.img`
    width: ${(props) => props.width || "100%"}; /* Permite definir um tamanho via props */
    max-height: 300px; /* Ajuste conforme necessário */
    object-fit: cover;
    border-radius: 10px; /* Deixa com cantos arredondados */
`;

export default HeadMain;
