import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import xpwindows from '../Assets/xpwindows.jpg'
import xpwinbg from '../Assets/xpwinbg.png'


function Login() {
  return (
    <BackgroundContainer>
      <GlassForm>
        <h2>Login</h2>
        <form>
          <input type="email" placeholder="Email" />
          <input type="password" placeholder="Senha" />
          <button type="submit">Entrar</button>
        </form>
      </GlassForm>
      
    </BackgroundContainer>
  );
}
const BackgroundContainer = styled.div`
  position: relative;
  background-image: url(${xpwindows});
  background-size: cover;
  background-position: center;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    inset: 0;
    background: rgba(0, 0, 0, 0.3);
    z-index: 1;
  }

  > * {
    position: relative;
    z-index: 2;
  }
`;

const GlassForm = styled.div`
  background: rgba(255, 255, 255, 0.4);
  border: 2px solid #000;
  box-shadow: inset -2px -2px 0 #fff, inset 2px 2px 0 #808080, 4px 4px 8px rgba(0, 0, 0, 0.5);
  padding: 40px;
  border-radius: 6px;
  width: 300px;
  font-family: 'Press Start 2P', monospace;
  color: #000;

  h2 {
    text-align: center;
    margin-bottom: 20px;
  }

  form {
    display: flex;
    flex-direction: column;
    gap: 15px;

    input {
      padding: 10px;
      border: 2px solid #000;
      background-color: rgba(255, 255, 255, 0.9);
      font-family: 'Press Start 2P', monospace;
    }

    button {
      padding: 10px;
      border: 2px solid #000;
      background-color: #c0c0c0;
      cursor: pointer;
      font-family: 'Press Start 2P', monospace;

      &:hover {
        background-color: #a0a0a0;
      }
    }
  }
`;




export default Login;
