import React from "react";
import styled from "styled-components";
import Categorias from "./Categorias";

function Postagens() {
  return (
    <PageWrapper>
      <TituloBlog>üìù Postagens no Blog</TituloBlog>
      <Container>
        <Categorias />
        <XPWindow>
          <TitleBar>
            <h2>Crie uma nova postagem</h2>
            <StyledButton>Criar</StyledButton>
          </TitleBar>
        </XPWindow>
      </Container>
    </PageWrapper>
  );
}

// DADOS MOCK (depois substitu√≠mos por chamada do back-end)
const MOCK_POSTAGENS = [
  {
    id: 1,
    titulo: "Primeiro post",
    conteudo: "Esse √© o conte√∫do do post de exemplo.",
    post_imagem: "https://via.placeholder.com/150",
    categorias_id: "Inspira√ß√£o",
    usuarios_id: 1,
    moods_id: 2,
  },
  {
    id: 2,
    titulo: "Reflex√£o do dia",
    conteudo: "Hoje foi um dia muito reflexivo...",
    post_imagem: "https://via.placeholder.com/150",
    categorias_id: "Reflex√µes",
    usuarios_id: 2,
    moods_id: 3,
  }
];

const PageWrapper = styled.div`
  background: linear-gradient(135deg, #d4d4ff, #f0e0ff);
  min-height: 100vh;
  padding: 40px 20px;
  font-family: 'Press Start 2P', monospace;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TituloBlog = styled.h1`
  font-size: 16px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 3px 3px #808080;
  padding: 10px 20px;
  margin-bottom: 30px;
  color: purple;
`;

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: flex-start;
  gap: 20px;
  flex-wrap: wrap;
`;

const XPWindow = styled.div`
  max-width: 600px;
  border: 2px solid #000;
  border-radius: 6px;
  background-color: #e0e0e0;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080, 3px 3px #999;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const TitleBar = styled.div`
  background-color: purple;
  color: white;
  padding: 20px;
  font-size: 20px;
  border-bottom: 2px solid #000;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;

  h2 {
    margin: 0;
    font-size: 14px;
    text-align: center;
  }
`;

const StyledButton = styled.button`
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 10px 20px;
  background-color: #fff;
  color: purple;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  cursor: pointer;

  &:hover {
    background-color: #f0f0f0;
  }
`;

export default Postagens;
