import styled from "styled-components";

const textoOpcoes = ['Home', 'Postagens', 'Categorias', 'Ideias', 'Sobre'];

function NavBar() {
    return (
        <NavContainer>
            <OpcoesWrapper>
                <Opcoes>
                    {textoOpcoes.map((texto) => (
                        <Opcao key={texto}><p>{texto}</p></Opcao>
                    ))}
                </Opcoes>
            </OpcoesWrapper>
        </NavContainer>
    )
}

const NavContainer = styled.nav`
    width: 100%;
    display: flex;
    justify-content: center;
    background: white;
`;

const OpcoesWrapper = styled.div`
    width: 100%;
   
    border: 2px solid black;
    display: flex;
    justify-content: center;
    padding: 15px; 
`;

const Opcoes = styled.ul`
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 20px; 
    list-style: none;
    background: #FFFFFF;
`;

const Opcao = styled.li`
    color: #2d2d34;
    font-size: 18px;
    cursor: pointer;
    text-align: center;

    &:hover {
        color: rgb(101, 101, 116);
    }
`;

export default NavBar;
