  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  backdrop-filter: blur(5px);
  background-color: rgba(0, 0, 0, 0.2);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 99;
`;

const PopupWindow = styled.div`
  width: 400px;
  background-color: #e0e0e0;
  border: 2px solid #000;
  box-shadow: 4px 4px #555;
  padding: 0;
  font-family: 'Press Start 2P', monospace;
`;

const PopupHeader = styled.div`
  background-color: purple;
  color: white;
  padding: 10px;
  font-size: 12px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const PopupButtons = styled.div`
  button {
    background: #fff;
    border: 1px solid #000;
    font-family: 'Press Start 2P', monospace;
    font-size: 10px;
    margin-left: 5px;
    cursor: pointer;
  }
`;

const PopupContent = styled.div`
  display: flex;
  flex-direction: column;
  padding: 20px;
  gap: 10px;

  label {
    font-size: 10px;
    color: purple;
  }

  input, textarea {
    font-family: 'Press Start 2P', monospace;
    font-size: 10px;
    padding: 5px;
    border: 2px solid #000;
    background-color: #fff;
  }
`;

const StyledButton = styled.button`
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 10px;
  margin-bottom: 15px;
  background-color: #fff;
  color: purple;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  cursor: pointer;
  width: 100%;

  &:hover {
    background-color: #f0f0f0;
  }
`;
const ImageUploadContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 10px;

  label {
    font-family: 'Press Start 2P', monospace;
    font-size: 10px;
    color: purple;
    padding: 6px 12px;
    background-color: #fff;
    border: 2px solid #000;
    box-shadow: 2px 2px #808080;
    cursor: pointer;

    &:hover {
      background-color: #f0f0f0;
    }
  }

  input[type="file"] {
    display: none;
  }
`;

const NomeArquivo = styled.p`
  font-size: 12px;
  margin-top: 5px;
  color: #333;
  font-family: 'Press Start 2P', monospace;
`;


const Dropdown = styled.ul`
  position: absolute;
 
  right: 0;
  left: 0;
  max-height: 200px; 
  overflow-y: auto; 
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  list-style: none;
  padding: 10px;
  z-index: 100;

 
  &::-webkit-scrollbar {
    width: 10px;
  }

  &::-webkit-scrollbar-track {
    background: #e0e0e0;
    border: 2px solid #000;
  }

  &::-webkit-scrollbar-thumb {
    background: purple;
    border: 2px solid #000;
  }

  &::-webkit-scrollbar-thumb:hover {
    background: #7a007a;
  }
`;


const DropdownItem = styled.li`
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 5px;
  color: purple;
  cursor: pointer;

  &:hover {
    background-color: #f0f0f0;
  }
`;





export default Postagens;
