import React, { useState } from 'react';
import styled from 'styled-components';
import { useAuth } from '../Contexts/AuthContext';
import { FaEdit } from 'react-icons/fa';
import { emojis } from '../utils/emojis'; // Lista de emojis

const Perfil = () => {
  const { usuario } = useAuth();

  if (!usuario) return <p>Você precisa estar logada para acessar o perfil.</p>;

  const [emoji, setEmoji] = useState('😀');
  const [editando, setEditando] = useState(false);
  const [mostrarDropdown, setMostrarDropdown] = useState(false);
  const [fotoNome, setFotoNome] = useState('');

  const [formData, setFormData] = useState({
    nome_exibicao: usuario.nome_exibicao || '',
    email: usuario.email || '',
    senha: '',
    bio: usuario.biografia || '',
    foto: usuario.foto || '',
  });

  const handleInputChange = (e) => {
    setFormData((f) => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleEmojiClick = (emj) => {
    setEmoji(emj);
    setMostrarDropdown(false);
  };

  const handleFotoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFotoNome(file.name);
      // Aqui você pode fazer upload real
    }
  };

  const salvarEdicao = (e) => {
    e.preventDefault();
    console.log('Salvando dados:', formData);
    setEditando(false);
  };

  return (
    <Container>
      <Titulo>Zona terrestre de @{usuario.username}</Titulo>

      <ProfileBox>
        <LeftSide>
          <UserPhoto src={usuario.foto || 'https://via.placeholder.com/150'} alt="Foto de perfil" />
          <EmojiBadge onClick={() => setMostrarDropdown((prev) => !prev)}>
            {emoji}
            {mostrarDropdown && (
              <Dropdown>
                {emojis.map((emj, i) => (
                  <DropdownItem key={i} onClick={() => handleEmojiClick(emj)}>
                    {emj}
                  </DropdownItem>
                ))}
              </Dropdown>
            )}
          </EmojiBadge>
          <DisplayName>{usuario.nome_exibicao}</DisplayName>
          <Username>@{usuario.username}</Username>
        </LeftSide>

        <RightSide>
          <Bio>{usuario.biografia}</Bio>

          <Redes>
            <p>📸 instagram.com/usuaria</p>
            <p>🐤 twitter.com/usuaria</p>
            <p>🎬 letterboxd.com/usuaria</p>
            <p>🎧 last.fm/user/usuaria</p>
          </Redes>

          <StyledButton onClick={() => setEditando(true)}>
            <FaEdit /> Editar perfil
          </StyledButton>
        </RightSide>
      </ProfileBox>

      {editando && (
        <BlurOverlay>
          <PopupWindow>
            <PopupHeader>
              Editar perfil
              <PopupButtons>
                <button onClick={() => setEditando(false)}>X</button>
              </PopupButtons>
            </PopupHeader>

            <form onSubmit={salvarEdicao}>
              <PopupContent>
                <label>Nome de exibição</label>
                <input name="nome_exibicao" value={formData.nome_exibicao} onChange={handleInputChange} />

                <label>Email</label>
                <input name="email" value={formData.email} onChange={handleInputChange} />

                <label>Senha (nova)</label>
                <input type="password" name="senha" value={formData.senha} onChange={handleInputChange} />

                <label>Bio</label>
                <textarea name="bio" value={formData.bio} onChange={handleInputChange} />

                <ImageUploadContainer>
                  <label htmlFor="upload">📷 Trocar foto</label>
                  <input type="file" id="upload" accept="image/*" onChange={handleFotoUpload} />
                  {fotoNome && <NomeArquivo>{fotoNome}</NomeArquivo>}
                </ImageUploadContainer>

                <StyledButton type="submit">Salvar</StyledButton>
              </PopupContent>
            </form>
          </PopupWindow>
        </BlurOverlay>
      )}
    </Container>
  );
};

export default Perfil;


// Styled components (mesma vibe retrô)

const Container = styled.div`
  display: flex;
  gap: 40px;
  padding: 30px;
  font-family: 'Press Start 2P', monospace;
  color: #4b0082;
`;

const LeftSide = styled.div`
  width: 250px;
  position: relative;
  text-align: center;
`;

const FotoWrapper = styled.div`
  position: relative;
  width: 200px;
  height: 200px;
  margin: 0 auto;
`;

const Foto = styled.img`
  width: 100%;
  height: 100%;
  border: 5px solid #4b0082;
  border-radius: 15px;
  object-fit: cover;
`;

const EmojiBalloon = styled.div`
  position: absolute;
  bottom: -30px;
  left: 50%;
  transform: translateX(-50%);
  background: #e0e0e0;
  border: 2px solid #4b0082;
  border-radius: 15px;
  padding: 6px 12px;
  cursor: pointer;
  user-select: none;

  &:hover > ul {
    display: flex;
  }
`;

const EmojiAtual = styled.span`
  font-size: 26px;
`;

const EmojiMenu = styled.ul`
  display: none;
  position: absolute;
  bottom: 40px;
  left: 50%;
  transform: translateX(-50%);
  background: #fff;
  border: 2px solid #4b0082;
  border-radius: 15px;
  padding: 6px;
  gap: 8px;
  list-style: none;
  cursor: pointer;
  box-shadow: 2px 2px #808080;
  z-index: 10;
`;

const EmojiOption = styled.li`
  font-size: 24px;
  padding: 4px;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const NomeDisplay = styled.h2`
  margin-top: 40px;
  font-size: 18px;
`;

const Username = styled.p`
  font-size: 14px;
  color: #666;
`;

const RightSide = styled.div`
  flex-grow: 1;
`;

const Bio = styled.p`
  font-size: 14px;
  margin-bottom: 24px;
  white-space: pre-wrap;
  min-height: 80px;
`;

const RedesSociais = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 30px;

  a {
    text-decoration: none;
    color: #4b0082;
    font-weight: bold;

    &:hover {
      text-decoration: underline;
    }
  }
`;

const EditarBtn = styled.button`
  font-family: 'Press Start 2P', monospace;
  font-size: 14px;
  padding: 10px 20px;
  background: #4b0082;
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;

  &:hover {
    background: #3a006f;
  }
`;

const ModalOverlay = styled.div`
  position: fixed;
  inset: 0;
  background: rgba(224, 224, 224, 0.7);
  backdrop-filter: blur(6px);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const Modal = styled.div`
  background: #fff;
  border: 4px solid #4b0082;
  border-radius: 12px;
  padding: 30px;
  width: 400px;

  form {
    display: flex;
    flex-direction: column;
    gap: 16px;

    label {
      font-size: 12px;
      color: #4b0082;
    }

    input, textarea {
      font-family: 'Press Start 2P', monospace;
      font-size: 12px;
      padding: 8px;
      border: 2px solid #4b0082;
      border-radius: 6px;
      outline: none;
      resize: vertical;
    }

    textarea {
      min-height: 60px;
    }
  }
`;

const Btns = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 12px;

  button {
    font-family: 'Press Start 2P', monospace;
    font-size: 14px;
    padding: 8px 12px;
    border: none;
    border-radius: 6px;
    cursor: pointer;

    &:first-child {
      background: #ccc;
      color: #333;
    }

    &:last-child {
      background: #4b0082;
      color: #fff;
    }

    &:last-child:hover {
      background: #3a006f;
    }
  }
`;
