import React, { useState } from 'react';
import styled from 'styled-components';
import { useAuth } from '../';

const emojisDisponiveis = ['ðŸ˜€', 'ðŸ˜Ž', 'ðŸŒŸ', 'ðŸ’«', 'ðŸš€', 'ðŸ‘½', 'âœ¨'];

const Perfil = () => {
  const { usuario } = useAuth();


  const [emoji, setEmoji] = useState('ðŸ˜€');
  const [editando, setEditando] = useState(false);

  const [formData, setFormData] = useState({
    nome_exibicao: usuario?.nome_exibicao || '',
    email: usuario?.email || '',
    senha: '', // senha nÃ£o mostra, sÃ³ para alterar
    bio: usuario?.biografia || '',
    foto: usuario?.foto || '',
  });

  // Se nÃ£o tiver usuÃ¡rio logado, sÃ³ avisa (return antes da renderizaÃ§Ã£o)
  if (!usuario) {
    return <p>VocÃª precisa estar logada para acessar o perfil.</p>;
  }

  const handleEmojiClick = (emj) => setEmoji(emj);

  const handleInputChange = (e) => {
    setFormData(f => ({ ...f, [e.target.name]: e.target.value }));
  };

  const abrirEdicao = () => setEditando(true);
  const fecharEdicao = () => setEditando(false);

  const salvarEdicao = (e) => {
    e.preventDefault();
    // Aqui entra sua lÃ³gica pra salvar o formData no backend e atualizar contexto
    console.log('Salvando dados:', formData);
    fecharEdicao();
  };

  return (
    <>
      <Container>
        <LeftSide>
          <FotoWrapper>
            <Foto src={formData.foto || '/default-avatar.png'} alt="Foto do usuÃ¡rio" />
            <EmojiBalloon>
              <EmojiAtual>{emoji}</EmojiAtual>
              <EmojiMenu>
                {emojisDisponiveis.map(emj => (
                  <EmojiOption key={emj} onClick={() => handleEmojiClick(emj)}>
                    {emj}
                  </EmojiOption>
                ))}
              </EmojiMenu>
            </EmojiBalloon>
          </FotoWrapper>
          <NomeDisplay>{formData.nome_exibicao || usuario.username}</NomeDisplay>
          <Username>@{usuario.username}</Username>
        </LeftSide>

        <RightSide>
          <Bio>{formData.bio || 'Nenhuma biografia adicionada.'}</Bio>
          <RedesSociais>
            {usuario.instagram && <a href={`https://instagram.com/${usuario.instagram}`} target="_blank" rel="noreferrer">Instagram</a>}
            {usuario.twitter && <a href={`https://twitter.com/${usuario.twitter}`} target="_blank" rel="noreferrer">Twitter</a>}
            {usuario.letterboxd && <a href={`https://letterboxd.com/${usuario.letterboxd}`} target="_blank" rel="noreferrer">Letterboxd</a>}
            {usuario.lastfm && <a href={`https://last.fm/user/${usuario.lastfm}`} target="_blank" rel="noreferrer">Last.fm</a>}
          </RedesSociais>

          <EditarBtn onClick={abrirEdicao}>Editar Perfil</EditarBtn>
        </RightSide>
      </Container>

      {editando && (
        <ModalOverlay>
          <Modal>
            <h2>Editar Perfil</h2>
            <form onSubmit={salvarEdicao}>
              <label>
                Nome de ExibiÃ§Ã£o:
                <input name="nome_exibicao" value={formData.nome_exibicao} onChange={handleInputChange} />
              </label>

              <label>
                Email:
                <input type="email" name="email" value={formData.email} onChange={handleInputChange} />
              </label>

              <label>
                Senha:
                <input type="password" name="senha" value={formData.senha} onChange={handleInputChange} placeholder="Deixe em branco para manter" />
              </label>

              <label>
                Biografia:
                <textarea name="bio" value={formData.bio} onChange={handleInputChange} />
              </label>

              <label>
                URL da Foto:
                <input name="foto" value={formData.foto} onChange={handleInputChange} />
              </label>

              <Btns>
                <button type="button" onClick={fecharEdicao}>Cancelar</button>
                <button type="submit">Salvar</button>
              </Btns>
            </form>
          </Modal>
        </ModalOverlay>
      )}
    </>
  );
};

export default Perfil;

// Styled components (mesma vibe retrÃ´)

const Container = styled.div`
  display: flex;
  gap: 40px;
  padding: 30px;
  font-family: 'Press Start 2P', monospace;
  color: #4b0082;
`;

const LeftSide = styled.div`
  width: 250px;
  position: relative;
  text-align: center;
`;

const FotoWrapper = styled.div`
  position: relative;
  width: 200px;
  height: 200px;
  margin: 0 auto;
`;

const Foto = styled.img`
  width: 100%;
  height: 100%;
  border: 5px solid #4b0082;
  border-radius: 15px;
  object-fit: cover;
`;

const EmojiBalloon = styled.div`
  position: absolute;
  bottom: -30px;
  left: 50%;
  transform: translateX(-50%);
  background: #e0e0e0;
  border: 2px solid #4b0082;
  border-radius: 15px;
  padding: 6px 12px;
  cursor: pointer;
  user-select: none;

  &:hover > ul {
    display: flex;
  }
`;

const EmojiAtual = styled.span`
  font-size: 26px;
`;

const EmojiMenu = styled.ul`
  display: none;
  position: absolute;
  bottom: 40px;
  left: 50%;
  transform: translateX(-50%);
  background: #fff;
  border: 2px solid #4b0082;
  border-radius: 15px;
  padding: 6px;
  gap: 8px;
  list-style: none;
  cursor: pointer;
  box-shadow: 2px 2px #808080;
  z-index: 10;
`;

const EmojiOption = styled.li`
  font-size: 24px;
  padding: 4px;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const NomeDisplay = styled.h2`
  margin-top: 40px;
  font-size: 18px;
`;

const Username = styled.p`
  font-size: 14px;
  color: #666;
`;

const RightSide = styled.div`
  flex-grow: 1;
`;

const Bio = styled.p`
  font-size: 14px;
  margin-bottom: 24px;
  white-space: pre-wrap;
  min-height: 80px;
`;

const RedesSociais = styled.div`
  display: flex;
  gap: 15px;
  margin-bottom: 30px;

  a {
    text-decoration: none;
    color: #4b0082;
    font-weight: bold;

    &:hover {
      text-decoration: underline;
    }
  }
`;

const EditarBtn = styled.button`
  font-family: 'Press Start 2P', monospace;
  font-size: 14px;
  padding: 10px 20px;
  background: #4b0082;
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;

  &:hover {
    background: #3a006f;
  }
`;

const ModalOverlay = styled.div`
  position: fixed;
  inset: 0;
  background: rgba(224, 224, 224, 0.7);
  backdrop-filter: blur(6px);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 1000;
`;

const Modal = styled.div`
  background: #fff;
  border: 4px solid #4b0082;
  border-radius: 12px;
  padding: 30px;
