import React, { useState } from 'react';
import styled from 'styled-components';

function Perfil() {
  // Dados mockados do usuário
  const [usuario, setUsuario] = useState({
    nome: 'Snack',
    email: 'fakesnak@email.com',
    bio: 'Amante de React e café ☕',
    imagem: 'https://robohash.org/snack',
  });

  // Estado para edição simples
  const [editando, setEditando] = useState(false);
  const [formData, setFormData] = useState(usuario);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const salvarPerfil = () => {
    setUsuario(formData);
    setEditando(false);
    alert('Perfil salvo! (mock)');
  };

  return (
    <Container>
      <Titulo>Perfil</Titulo>

      <Foto src={usuario.imagem} alt="Foto do usuário" />

      {editando ? (
        <>
          <Label>Nome</Label>
          <Input name="nome" value={formData.nome} onChange={handleChange} />

          <Label>Email</Label>
          <Input name="email" value={formData.email} onChange={handleChange} />

          <Label>Biografia</Label>
          <Textarea name="bio" value={formData.bio} onChange={handleChange} />

          <Botao onClick={salvarPerfil}>Salvar</Botao>
          <Botao onClick={() => setEditando(false)}>Cancelar</Botao>
        </>
      ) : (
        <>
          <Info><strong>Nome:</strong> {usuario.nome}</Info>
          <Info><strong>Email:</strong> {usuario.email}</Info>
          <Info><strong>Bio:</strong> {usuario.bio}</Info>
          <Botao onClick={() => setEditando(true)}>Editar Perfil</Botao>
        </>
      )}
    </Container>
  );
}

const Container = styled.div`
  max-width: 400px;
  margin: 30px auto;
  font-family: 'Press Start 2P', monospace;
  background: #e0e0e0;
  padding: 20px;
  border: 2px solid #000;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
`;

const Titulo = styled.h1`
  text-align: center;
  margin-bottom: 20px;
`;

const Foto = styled.img`
  display: block;
  width: 150px;
  height: 150px;
  border: 3px solid #000;
  border-radius: 10px;
  margin: 0 auto 20px;
  object-fit: cover;
`;

const Label = styled.label`
  display: block;
  margin-top: 10px;
  font-weight: bold;
`;

const Input = styled.input`
  width: 100%;
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 6px;
  margin-top: 4px;
  border: 2px solid #000;
  box-shadow: inset -1px -1px #fff, inset 1px 1px #808080;
  background: #fff;
`;

const Textarea = styled.textarea`
  width: 100%;
  height: 60px;
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 6px;
  margin-top: 4px;
  border: 2px solid #000;
  box-shadow: inset -1px -1px #fff, inset 1px 1px #808080;
  background: #fff;
`;

const Info = styled.p`
  margin: 10px 0;
`;

const Botao = styled.button`
  margin-top: 15px;
  margin-right: 10px;
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 8px 15px;
  border: 2px solid #000;
  background: #d0d0d0;
  cursor: pointer;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;

  &:hover {
    background: #b0b0b0;
  }
`;

export default Perfil;
