import Input from '../Input'
import styled from 'styled-components'
import { useState } from 'react'
import { categoriasPesquisadas } from '../../Componentes'

const PesquisaContainer = styled.section`
    background-image: linear-gradient(90deg, #002F52 35%, #326589 165%);
    color: #FFF;
    text-align: center;
    padding: 85px 0;
    height: 270px;
    width: 100%;

`
const Resultado = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 20px;
    cursor: pointer;

    p {
        width: 200px;
    }

  
    &:hover {
        border: 1px solid white;
    }
`




function Pesquisa() {
    const [categoriasPesquisadas, setcategoriasPesquisadas] = useState([])

    return (
        <PesquisaContainer>
         
            <Input
                placeholder="Procurando algum tema?"
                onBlur={evento => {
                    const textoDigitado = evento.target.value
                    const resultadoPesquisa = categoriasPesquisadas.filter(post => post.nome.includes(textoDigitado))
                    setcategoriasPesquisadas(resultadoPesquisa)
                }}
            />
            {categoriasPesquisadas.map(post => (
                <Resultado>
                    <p>{post.nome}</p>
                    
                </Resultado>

            ))}

        </PesquisaContainer>
    )
}

export default Pesquisa
