import React, { useState } from 'react';
import styled from 'styled-components';
import { useAuth } from '../Contexts/AuthContext';
import { FaEdit } from 'react-icons/fa';
import { emojis } from '../Componentes/Utils/emojisLista';
import { FaInstagram, FaTwitter, FaLink } from 'react-icons/fa';


const Perfil = () => {
  const { usuario } = useAuth();

  // 👇 Mova os hooks aqui pra fora antes do `if`
  const [emoji, setEmoji] = useState('😀');
  const [editando, setEditando] = useState(false);
  const [mostrarDropdown, setMostrarDropdown] = useState(false);
  const [fotoNome, setFotoNome] = useState('');

  const [formData, setFormData] = useState({
    nome_exibicao: usuario?.nome_exibicao || '',
    email: usuario?.email || '',
    senha: '',
    bio: usuario?.biografia || '',
    foto: usuario?.foto || '',
  });


  if (!usuario) return <p>Você precisa estar logada para acessar o perfil.</p>;

  const handleInputChange = (e) => {
    setFormData((f) => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleEmojiClick = (emj) => {
    setEmoji(emj);
    setMostrarDropdown(false);
  };

  const handleFotoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFotoNome(file.name);
      // Aqui você pode fazer o upload real depois se quiser
    }
  };

  const salvarEdicao = (e) => {
    e.preventDefault();
    console.log('Salvando dados:', formData);
    setEditando(false);
  };

 
  return (
    <PageWrapper>
    <Container>
      <Titulo>Zona terrestre de @{usuario.username}</Titulo>

      <ProfileBox>
        <LeftSide>
          <UserPhoto src={usuario.foto || 'https://via.placeholder.com/150'} alt="Foto de perfil" />
          <EmojiBadge onClick={() => setMostrarDropdown((prev) => !prev)}>
            {emoji}
            {mostrarDropdown && (
              <Dropdown>
                {emojis.map((emj, i) => (
                  <DropdownItem key={i} onClick={() => handleEmojiClick(emj)}>
                    {emj}
                  </DropdownItem>
                ))}
              </Dropdown>
            )}
          </EmojiBadge>
          <DisplayName>{usuario.nome_exibicao}</DisplayName>
          <Username>@{usuario.username}</Username>
        </LeftSide>

        <RightSide>
          <Bio>{usuario.biografia}</Bio>

          <Redes>
            <p>📸 instagram.com/usuaria</p>
            <p>🐤 twitter.com/usuaria</p>
            <p>🎬 letterboxd.com/usuaria</p>
            <p>🎧 last.fm/user/usuaria</p>
          </Redes>

          <StyledButton onClick={() => setEditando(true)}>
            <FaEdit /> Editar perfil
          </StyledButton>
        </RightSide>
      </ProfileBox>

      {editando && (
        <BlurOverlay>
          <PopupWindow>
            <PopupHeader>
              Editar perfil
              <PopupButtons>
                <button onClick={() => setEditando(false)}>X</button>
              </PopupButtons>
            </PopupHeader>

            <form onSubmit={salvarEdicao}>
              <PopupContent>
                <label>Nome de exibição</label>
                <input name="nome_exibicao" value={formData.nome_exibicao} onChange={handleInputChange} />

                <label>Email</label>
                <input name="email" value={formData.email} onChange={handleInputChange} />

                <label>Senha (nova)</label>
                <input type="password" name="senha" value={formData.senha} onChange={handleInputChange} />

                <label>Bio</label>
                <textarea name="bio" value={formData.bio} onChange={handleInputChange} />

                <ImageUploadContainer>
                  <label htmlFor="upload">📷 Trocar foto</label>
                  <input type="file" id="upload" accept="image/*" onChange={handleFotoUpload} />
                  {fotoNome && <NomeArquivo>{fotoNome}</NomeArquivo>}
                </ImageUploadContainer>

                <StyledButton type="submit">Salvar</StyledButton>
              </PopupContent>
            </form>
          </PopupWindow>
        </BlurOverlay>
      )}
    </Container>
    </PageWrapper>
  );
};

export default Perfil;



const PageWrapper = styled.div`
  background: linear-gradient(135deg, #d4d4ff, #f0e0ff);
  min-height: 100vh;
  padding: 40px 20px;
  font-family: 'Press Start 2P', monospace;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

// Título retrô roxo com sombra
export const Titulo = styled.h1`

  font-size: 16px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 3px 3px #808080;
  padding: 10px 20px;
  margin-bottom: 30px;
  color: purple;
  text-align: center;
  font-family: 'Press Start 2P', monospace;
`;

// Container geral
export const Container = styled.div`
  padding: 30px;
  font-family: 'Press Start 2P', monospace;
`;

// Caixa principal dividida em 2 lados
export const ProfileBox = styled.div`
  display: flex;
  gap: 40px;
  align-items: flex-start;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

// Lado esquerdo: imagem + nome
export const LeftSide = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

// Foto do usuário
export const UserPhoto = styled.img`
  width: 150px;
  height: 150px;
  border: 3px solid #000;
  box-shadow: 4px 4px #555;
  object-fit: cover;
  background-color: #fff;
`;

// Badge com emoji
export const EmojiBadge = styled.div`
  font-size: 30px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  padding: 5px 10px;
  margin-top: -20px;
  margin-bottom: 10px;
  cursor: pointer;
`;

// Nome e username
export const DisplayName = styled.p`
  font-size: 12px;
  margin: 5px 0;
  color: purple;
`;

export const Username = styled.p`
  font-size: 10px;
  color: #555;
`;

// Lado direito: bio + redes + botão
export const RightSide = styled.div`
  flex: 1;
`;

export const Bio = styled.p`
  font-size: 10px;
  color: #333;
  margin-bottom: 20px;
`;

export const Redes = styled.div`
  font-size: 10px;
  color: purple;
  margin-bottom: 20px;

  p {
    margin-bottom: 6px;
  }
`;

// Botão estilizado retrô
export const StyledButton = styled.button`
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 10px;
  background-color: #fff;
  color: purple;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  cursor: pointer;
  width: 100%;

  &:hover {
    background-color: #f0f0f0;
  }
`;

// Fundo em blur para o formulário
export const BlurOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  backdrop-filter: blur(5px);
  background-color: rgba(0, 0, 0, 0.2);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 99;
`;

// Janela popup
export const PopupWindow = styled.div`
  width: 400px;
  background-color: #e0e0e0;
  border: 2px solid #000;
  box-shadow: 4px 4px #555;
  padding: 0;
  font-family: 'Press Start 2P', monospace;
`;

export const PopupHeader = styled.div`
  background-color: purple;
  color: white;
  padding: 10px;
  font-size: 12px;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

export const PopupButtons = styled.div`
  button {
    background: #fff;
    border: 1px solid #000;
    font-family: 'Press Start 2P', monospace;
    font-size: 10px;
    margin-left: 5px;
    cursor: pointer;
  }
`;

export const PopupContent = styled.div`
  display: flex;
  flex-direction: column;
  padding: 20px;
  gap: 10px;

  label {
    font-size: 10px;
    color: purple;
  }

  input, textarea {
    font-family: 'Press Start 2P', monospace;
    font-size: 10px;
    padding: 5px;
    border: 2px solid #000;
    background-color: #fff;
  }
`;

// Upload de imagem
export const ImageUploadContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  gap: 10px;

  label {
    font-family: 'Press Start 2P', monospace;
    font-size: 10px;
    color: purple;
    padding: 6px 12px;
    background-color: #fff;
    border: 2px solid #000;
    box-shadow: 2px 2px #808080;
    cursor: pointer;

    &:hover {
      background-color: #f0f0f0;
    }
  }

  input[type="file"] {
    display: none;
  }
`;

export const NomeArquivo = styled.p`
  font-size: 12px;
  margin-top: 5px;
  color: #333;
  font-family: 'Press Start 2P', monospace;
`;

// Dropdown de emojis
export const Dropdown = styled.ul`
  position: absolute;
  max-height: 200px;
  overflow-y: auto;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  list-style: none;
  padding: 10px;
  z-index: 100;
  margin-top: 5px;

  &::-webkit-scrollbar {
    width: 10px;
  }

  &::-webkit-scrollbar-track {
    background: #e0e0e0;
    border: 2px solid #000;
  }

  &::-webkit-scrollbar-thumb {
    background: purple;
    border: 2px solid #000;
  }

  &::-webkit-scrollbar-thumb:hover {
    background: #7a007a;
  }
`;

export const DropdownItem = styled.li`
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 5px;
  color: purple;
  cursor: pointer;

  &:hover {
    background-color: #f0f0f0;
  }
`;
