import React from "react";
import styled from "styled-components";


function Postagens() {
  return (
    <CriarPostContainer>
      <XPWindow>
        <TitleBar>
          
        </TitleBar>
      </XPWindow>
      <Content>
      <h2>Crie uma nova postagem</h2>
      <button>Nova Postagem</button>
      </Content>
   </CriarPostContainer>)
}


const XPWindow = styled.div`
  max-width: 100%;
  margin: 20px 0;
  border: 2px solid #000;
  background-color: #e0e0e0;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
  font-family: 'Press Start 2P', monospace;
`;

const TitleBar = styled.div`
  background-color: purple;
  color: white;
  padding: 10px;
  font-size: 20px;
  border-bottom: 2px solid #000;
`;

const Content = styled.div`
  padding: 20px;
  color: #000;

  h2 {
    margin-top: 20px;
    font-size: 18px;
  }

  p {
    font-size: 16px;
    line-height: 1.8;
    margin-top: 10px;
  }

  ul {
     font-family: 'Press Start 2P', monospace; 
    font-size: 16px;
    list-style-type: disc; 
    padding-left: 20px; 
  }

  a {
    color: purple;
    text-decoration: none;
  }

  a:hover {
    text-decoration: underline;
  }
`;

const CriarPostContainer = styled.section`
  margin: 20px; 
  padding: 15px;
  background: #89A8B2;
  border-radius: 8px;
  text-align: center;
  width: 100vw; 
  max-width: 600px; 
  height: auto; 
  overflow: hidden; 
  box-sizing: border-box;

  button {
    padding: 10px;
    border: none;
    background: #2D2D34;
    color: white;
    border-radius: 5px;
    cursor: pointer;
  }
`;

export default Postagens;
