import styled from 'styled-components';
import { useAuth } from '../Contexts/AuthContext';
import React, { useState, useEffect } from 'react';


const Perfil = () => {
  const { usuario } = useAuth();

  // Inicializa emoji e bio com o que tiver no localStorage ou do usu√°rio
  const [emoji, setEmoji] = useState(() => localStorage.getItem('perfilEmoji') || 'üòÄ');
  const [editando, setEditando] = useState(false);
  const [mostrarDropdown, setMostrarDropdown] = useState(false);
  const [fotoNome, setFotoNome] = useState('');

  const [formData, setFormData] = useState(() => {
    const savedBio = localStorage.getItem('perfilBio');
    return {
      nome_exibicao: usuario?.nome_exibicao || '',
      email: usuario?.email || '',
      senha: '',
      bio: savedBio || usuario?.biografia || '',
      foto: usuario?.foto || `https://robohash.org/${usuario?.username}.png?set=cat`,
    };
  });

  // Hooks devem vir aqui, antes do return
  useEffect(() => {
    localStorage.setItem('perfilEmoji', emoji);
  }, [emoji]);

  useEffect(() => {
    localStorage.setItem('perfilBio', formData.bio);
  }, [formData.bio]);

  if (!usuario) return <p>Voc√™ precisa estar logada para acessar o perfil.</p>;

  // Handlers
  const handleInputChange = (e) => {
    setFormData(f => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleEmojiClick = (emj) => {
    setEmoji(emj);
    setMostrarDropdown(false);
  };

  const handleFotoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFotoNome(file.name);
      // Aqui s√≥ guardamos o nome, upload real precisa deploy/backend
      // Se quiser, pode gerar uma URL tempor√°ria para preview:
      const url = URL.createObjectURL(file);
      setFormData(f => ({ ...f, foto: url }));
    }
  };

  const salvarEdicao = (e) => {
    e.preventDefault();
    // Aqui voc√™ faria a chamada para salvar no backend
    console.log('Salvando dados:', { emoji, ...formData });
    setEditando(false);
    alert('Perfil salvo (simulado)');
  };

  return (
    <div>
      <h2>Perfil de {usuario.nome_exibicao}</h2>

      <div>
        <strong>Emoji:</strong> <span style={{ fontSize: '2rem' }}>{emoji}</span>{' '}
        <button onClick={() => setMostrarDropdown(!mostrarDropdown)}>
          Alterar Emoji
        </button>
        {mostrarDropdown && (
          <div style={{ border: '1px solid #ccc', padding: 10, maxWidth: 200 }}>
            {['üòÄ', 'üòé', 'üòç', 'üò¢', 'üò°'].map(emj => (
              <button
                key={emj}
                onClick={() => handleEmojiClick(emj)}
                style={{ fontSize: '1.5rem', margin: 5 }}
              >
                {emj}
              </button>
            ))}
          </div>
        )}
      </div>

      <form onSubmit={salvarEdicao}>
        <div>
          <label>
            Nome de Exibi√ß√£o:
            <input
              type="text"
              name="nome_exibicao"
              value={formData.nome_exibicao}
              onChange={handleInputChange}
              disabled={!editando}
            />
          </label>
        </div>

        <div>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              disabled={!editando}
            />
          </label>
        </div>

        <div>
          <label>
            Biografia:
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleInputChange}
              disabled={!editando}
            />
          </label>
        </div>

        <div>
          <label>
            Foto de Perfil:
            <input type="file" onChange={handleFotoUpload} disabled={!editando} />
          </label>
          {fotoNome && <p>Arquivo selecionado: {fotoNome}</p>}
          {formData.foto && (
            <img
              src={formData.foto}
              alt="Foto de perfil"
              style={{ width: 150, height: 150, objectFit: 'cover', borderRadius: '50%' }}
            />
          )}
        </div>

        <div style={{ marginTop: 15 }}>
          {!editando && (
            <button type="button" onClick={() => setEditando(true)}>
              Editar Perfil
            </button>
          )}
          {editando && (
            <>
              <button type="submit">Salvar</button>
              <button type="button" onClick={() => setEditando(false)}>
                Cancelar
              </button>
            </>
          )}
        </div>
      </form>
    </div>
  );
};



export default Perfil;



export const Container = styled.div`
  max-width: 600px;
  margin: 30px auto;
  background: #c0c0c0; /* cinza cl√°ssico do Windows 98 */
  border: 2px solid #000;
  box-shadow:
    inset 2px 2px 0 #fff,
    inset -2px -2px 0 #404040;
  padding: 20px;
  font-family: 'Press Start 2P', cursive;
  color: #000;
  user-select: none;
`;

export const Title = styled.h2`
  text-align: center;
  margin-bottom: 20px;
  font-size: 1.1rem;
  color: #000080;
  text-shadow: 1px 1px #fff;
`;

export const EmojiSection = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  margin-bottom: 15px;
`;

export const EmojiDisplay = styled.span`
  font-size: 2.5rem;
  user-select: none;
`;

export const EmojiButton = styled.button`
  font-family: 'Press Start 2P', cursive;
  font-size: 0.7rem;
  padding: 6px 12px;
  background: #c0c0c0;
  border: 2px solid #000;
  box-shadow:
    inset 2px 2px 0 #fff,
    inset -2px -2px 0 #404040;
  cursor: pointer;
  transition: background 0.1s ease;

  &:active {
    box-shadow:
      inset -2px -2px 0 #fff,
      inset 2px 2px 0 #404040;
  }
`;

export const EmojiDropdown = styled.div`
  margin-top: 10px;
  padding: 8px;
  background: #c0c0c0;
  border: 2px solid #000;
  box-shadow:
    inset 2px 2px 0 #fff,
    inset -2px -2px 0 #404040;
  display: flex;
  gap: 10px;
  flex-wrap: wrap;
`;

export const EmojiOption = styled.button`
  font-family: 'Press Start 2P', cursive;
  font-size: 1.5rem;
  background: #c0c0c0;
  border: 2px solid #000;
  box-shadow:
    inset 2px 2px 0 #fff,
    inset -2px -2px 0 #404040;
  cursor: pointer;
  padding: 3px 6px;
  border-radius: 0;
  transition: background 0.1s ease;

  &:active {
    box-shadow:
      inset -2px -2px 0 #fff,
      inset 2px 2px 0 #404040;
  }
`;

export const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 12px;
`;

export const Label = styled.label`
  font-family: 'Press Start 2P', cursive;
  font-size: 0.7rem;
  color: #000;
  display: flex;
  flex-direction: column;
  user-select: none;
`;

export const Input = styled.input`
  font-family: 'Press Start 2P', cursive;
  font-size: 0.7rem;
  margin-top: 4px;
  padding: 6px;
  border: 2px solid #000;
  background: #c0c0c0;
  box-shadow:
    inset 2px 2px 0 #fff,
    inset -2px -2px 0 #404040;
  color: #000;

  &:disabled {
    background: #e0e0e0;
    color: #888;
  }
`;

export const TextArea = styled.textarea`
  font-family: 'Press Start 2P', cursive;
  font-size: 0.7rem;
  margin-top: 4px;
  padding: 6px;
  border: 2px solid #000;
  background: #c0c0c0;
  box-shadow:
    inset 2px 2px 0 #fff,
    inset -2px -2px 0 #404040;
  color: #000;
  resize: vertical;

  &:disabled {
    background: #e0e0e0;
    color: #888;
  }
`;

export const FotoPreview = styled.img`
  width: 140px;
  height: 140px;
  object-fit: cover;
  border: 3px solid #000;
  margin-top: 8px;
  user-select: none;
`;

export const ButtonsWrapper = styled.div`
  display: flex;
  gap: 12px;
  margin-top: 20px;
`;

export const Button = styled.button`
  font-family: 'Press Start 2P', cursive;
  font-size: 0.7rem;
  padding: 8px 14px;
  background: #c0c0c0;
  border: 2px solid #000;
  box-shadow:
    inset 2px 2px 0 #fff,
    inset -2px -2px 0 #404040;
  cursor: pointer;
  transition: background 0.1s ease;

  &:active {
    box-shadow:
      inset -2px -2px 0 #fff,
      inset 2px 2px 0 #404040;
  }

  &:disabled {
    background: #999;
