import React, { useEffect, useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../../contexts/AuthContext";

const Postagens = () => {
  const [posts, setPosts] = useState([]);
  const [categoriaSelecionada, setCategoriaSelecionada] = useState("");
  const [mostrarForm, setMostrarForm] = useState(false);
  const [titulo, setTitulo] = useState("");
  const [conteudo, setConteudo] = useState("");
  const [humorSelecionado, setHumorSelecionado] = useState("");

  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await axios.get("http://localhost:3001/posts");
        setPosts(res.data);
      } catch (err) {
        console.error("Erro ao buscar posts:", err);
      }
    };

    fetchPosts();
  }, []);

  const handleCategoriaClick = (categoria) => {
    setCategoriaSelecionada(categoria === categoriaSelecionada ? "" : categoria);
  };

  const handlePostSubmit = async () => {
    if (!currentUser) {
      alert("VocÃª precisa estar logada para criar um post.");
      return;
    }

    if (!titulo || !conteudo || !categoriaSelecionada || !humorSelecionado) {
      alert("Preencha todos os campos!");
      return;
    }

    try {
      await axios.post("http://localhost:3001/posts", {
        titulo,
        conteudo,
        categoria: categoriaSelecionada,
        usuarioId: currentUser.id,
        humorId: humorSelecionado,
      });

      // Atualizar lista de posts apÃ³s criaÃ§Ã£o
      const res = await axios.get("http://localhost:3001/posts");
      setPosts(res.data);

      // Resetar form
      setTitulo("");
      setConteudo("");
      setCategoriaSelecionada("");
      setHumorSelecionado("");
      setMostrarForm(false);
    } catch (err) {
      console.error("Erro ao criar post:", err);
    }
  };

  const categorias = [...new Set(posts.map((post) => post.categoria))];

  const postsFiltrados = categoriaSelecionada
    ? posts.filter((post) => post.categoria === categoriaSelecionada)
    : posts;

  return (
    <Container>
      <Header>
        {categorias.map((categoria, index) => (
          <CategoriaButton
            key={index}
            onClick={() => handleCategoriaClick(categoria)}
            ativo={categoria === categoriaSelecionada}
          >
            {categoria}
          </CategoriaButton>
        ))}

        <CriarPostButton onClick={() => {
          if (!currentUser) {
            alert("VocÃª precisa estar logada para criar um post!");
            return;
          }
          setMostrarForm(true);
        }}>
          Criar Post
        </CriarPostButton>
      </Header>

      {mostrarForm && (
        <BlurBackground>
          <XPWindow>
            <PostForm>
              <Input
                placeholder="TÃ­tulo"
                value={titulo}
                onChange={(e) => setTitulo(e.target.value)}
              />
              <TextArea
                placeholder="ConteÃºdo"
                value={conteudo}
                onChange={(e) => setConteudo(e.target.value)}
              />
              <Select
                value={categoriaSelecionada}
                onChange={(e) => setCategoriaSelecionada(e.target.value)}
              >
                <option value="">Selecione a categoria</option>
                {categorias.map((categoria, index) => (
                  <option key={index} value={categoria}>
                    {categoria}
                  </option>
                ))}
              </Select>
              <Select
                value={humorSelecionado}
                onChange={(e) => setHumorSelecionado(e.target.value)}
              >
                <option value="">Selecione seu humor</option>
                <option value="1">ðŸ˜Š Feliz</option>
                <option value="2">ðŸ˜¢ Triste</option>
                <option value="3">ðŸ˜  Irritada</option>
                {/* Substitua pelos valores reais do seu banco */}
              </Select>
              <CriarPostButton onClick={handlePostSubmit}>Postar</CriarPostButton>
              <CriarPostButton onClick={() => setMostrarForm(false)}>Cancelar</CriarPostButton>
            </PostForm>
          </XPWindow>
        </BlurBackground>
      )}

      {postsFiltrados.map((post) => (
        <XPWindow key={post.id}>
          <PostCard>
            <PostTitulo>{post.titulo}</PostTitulo>
            <PostConteudo>{post.conteudo}</PostConteudo>
            <PostDetalhes>
              {post.categoria} | Por: {post.usuario?.nome_exibicao} | Se sentindo: {post.humor?.emoji} {post.humor?.descricao}
            </PostDetalhes>
          </PostCard>
        </XPWindow>
      ))}
    </Container>
  );
};

export default Postagens;


export const Container = styled.div`
  padding: 2rem;
  font-family: "Press Start 2P", cursive;
`;

export const XPWindow = styled.div`
  background-color: #c0c0c0;
  border: 2px solid #000;
  box-shadow: 4px 4px #808080;
  margin-bottom: 2rem;
  padding: 1rem;
  width: 100%;
`;

export const Header = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  margin-bottom: 2rem;
`;

export const TituloCategoria = styled.h2`
  font-size: 1rem;
  margin-bottom: 1rem;
`;

export const PostCard = styled.div`
  background-color: white;
  padding: 1rem;
  border: 2px inset #000;
`;

export const PostTitulo = styled.h3`
  font-size: 1rem;
  margin-bottom: 0.5rem;
`;

export const PostConteudo = styled.p`
  font-size: 0.875rem;
  margin-bottom: 0.5rem;
`;

export const PostDetalhes = styled.div`
  font-size: 0.75rem;
  color: #333;
`;

export const CategoriaButton = styled.button`
  font-family: "Press Start 2P", cursive;
  font-size: 0.75rem;
  padding: 0.5rem 1rem;
  background-color: ${({ ativo }) => (ativo ? "#000080" : "#f0f0f0")};
  color: ${({ ativo }) => (ativo ? "#fff" : "#000")};
  border: 2px outset #000;
  cursor: pointer;

  &:hover {
    background-color: #000080;
    color: #fff;
  }
`;

export const CriarPostButton = styled.button`
  font-family: "Press Start 2P", cursive;
  font-size: 0.75rem;
  padding: 0.5rem 1rem;
  background-color: #f0f0f0;
  border: 2px outset #000;
  cursor: pointer;

  &:hover {
    background-color: #000080;
    color: #fff;
  }
`;

export const PostForm = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

export const Input = styled.input`
  font-family: "Press Start 2P", cursive;
  font-size: 0.75rem;
  padding: 0.5rem;
  border: 2px inset #000;
`;

export const TextArea = styled.textarea`
  font-family: "Press Start 2P", cursive;
  font-size: 0.75rem;
  padding: 0.5rem;
  border: 2px inset #000;
`;

export const Select = styled.select`
  font-family: "Press Start 2P", cursive;
  font-size: 0.75rem;
  padding: 0.5rem;
  border: 2px inset #000;
`;

export const BlurBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  backdrop-filter: blur(3px);
  background-color: rgba(0, 0, 0, 0.3);
  z-index: 10;
  display: flex;
  justify-content: center;
  align-items: center;
`;
