import styled from 'styled-components';

const ButtonWrapper = styled.div`
  border: 1px solid rgb(1, 63, 102);
  border-radius: 3px;
  display: inline-block;
  padding: 2px 0 4px 0;
  background: linear-gradient(
    to bottom,
#f6f6f6 0%,
#ffffff 6%,
    #fffdfe 12%,
    #fbf9fa 18%,
    #f8f7f5 41%,
    #f7f3f0 47%,
    #f8f3ef 82%,
    #e7e0d8 94%,
    #dad3c9 100%
  );
`;

const StyledButton = styled.button`
  padding: 3px 15px;
  border: 2px solid transparent;
  background: transparent;
  cursor: pointer;
  font-family: inherit;

  &:hover,
  &:focus {
    outline: none;
    border: 2px solid #b9d0f0;
  }

  &:active {
    background: linear-gradient(
      to bottom,
      #dad3c9 0%,
      #e7e0d8 6%,
      #f8f3ef 18%,
      #f7f3f0 53%,
      #f8f7f5 59%,
      #fbf9fa 82%,
      #fffdfe 88%,
      #ffffff 94%,
      #f6f6f6 100%
    );
  }
`;

export const XPButton = ({ children, onClick, type = 'button' }) => {
  return (
    <ButtonWrapper>
      <StyledButton onClick={onClick} type={type}>
        {children}
      </StyledButton>
    </ButtonWrapper>
  );
};
