import React from "react";
import styled from "styled-components";

const MainPage = ({ termoBusca, categorias, posts }) => {
  // Se o usu√°rio n√£o digitou nada, mostrar mensagem padr√£o

  console.log("üîç Termo de busca:", termoBusca);
  console.log("üìÇ Categorias:", categorias);
  console.log("üìù Posts:", posts);

  if (!termoBusca) {
    return <MainContainer></MainContainer>;
  }

  // Filtrar categorias e posts conforme a busca
  const resultados = [...categorias, ...posts].filter(item => {
    console.log("verificando:", item.nome);
    return item.nome.toLowerCase().includes(termoBusca.toLowerCase())
 } );

  console.log("üéØ Resultados filtrados:", resultados);

  return (
    <MainContainer>
      {resultados.length === 0 ? (
        <p>Nenhum resultado encontrado.</p>
      ) : (
        resultados.map((item) => (
          <Resultado key={item.id}>{item.nome}</Resultado>
        ))
      )}
    </MainContainer>
  );
};

const MainContainer = styled.section`
  padding: 20px;
`;

const Resultado = styled.div`
  padding: 10px;
  border-bottom: 1px solid #ccc;
`;

export default MainPage;
