import styled from "styled-components";

const textoOpcoes = ['Home', 'Postagens', 'Categorias', 'Ideias', 'Sobre'];

function NavBar() {
    return (
        <NavContainer>
            <Opcoes>
                {textoOpcoes.map((texto) => (
                    <Opcao key={texto}><p>{texto}</p></Opcao>
                ))}
            </Opcoes>
        </NavContainer>
    )
}

const NavContainer = styled.nav`
    width: 100%;
    display: flex;
    justify-content: center;
    background: white;
`;

const Opcoes = styled.ul`
    display: flex;
    justify-content: space-around;
    align-items: center;
    gap: 5px;
    width: 60%;
    min-width: 500px;
    max-width: 800px;
    list-style: none;
    background: white;

    border: 2px solid black; /* Borda apenas ao redor da lista */
    padding: 10px 20px; /* Espaço interno para a borda */
`;

const Opcao = styled.li`
    color: #2d2d34;
    font-size: 18px;
    cursor: pointer;
    text-align: center;
    padding: 10px 15px;
    box-sizing: border-box;

    &:hover {
        color: rgb(101, 101, 116);
    }
`;

export default NavBar;
