import React from "react";
import styled from "styled-components";

const MainPage = ({ termoBusca, categorias, posts }) => {
  // Se o usuário não digitou nada, mostrar mensagem padrão
  if (!termoBusca) {
    return <MainContainer></MainContainer>;
  }

  // Filtrar categorias e posts conforme a busca
  const resultados = [...categorias, ...posts].filter(item =>
    item.nome.toLowerCase().includes(termoBusca.toLowerCase())
  );

  return (
    <MainContainer>
      {resultados.length === 0 ? (
        <p>Nenhum resultado encontrado.</p>
      ) : (
        resultados.map((item) => (
          <Resultado key={item.id}>{item.nome}</Resultado>
        ))
      )}
    </MainContainer>
  );
};

const MainContainer = styled.section`
  padding: 20px;
`;

const Resultado = styled.div`
  padding: 10px;
  border-bottom: 1px solid #ccc;
`;

export default MainPage;
