import { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import menu from '../../Assets/menu.png';
import search from '../../Assets/search.png';
import { Link } from 'react-router-dom';


function IconesNavBar() {
    const [menuAberto, setMenuAberto] = useState(false);
    const menuRef = useRef(null);

    const [mostrarBusca, setMostrarBusca] = useState(false);
    const toggleBusca = () => {
        setMostrarBusca((prev) => !prev);
    };


    const handleMenuClick = () => {
        setMenuAberto((prev) => !prev);
    };

    useEffect(() => {
        function handleClickFora(event) {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                setMenuAberto(false);
            }
        }

        document.addEventListener('mousedown', handleClickFora);

        return () => {
            document.removeEventListener('mousedown', handleClickFora);
        };
    }, []);

    

    return (
        <Container ref={menuRef}>
            <Icones>
                <Icone ref={menuRef}>
  <img src={search} alt='ícone de busca' onClick={toggleBusca} />
  {mostrarBusca && (
    <SearchBox>
      <input type="text" placeholder="Buscar..." />
    </SearchBox>
  )}
</Icone>


                <Icone onClick={handleMenuClick}>
                    <img src={menu} alt='ícone de menu' />
                    {menuAberto && (
                        <Dropdown>
                            <DropdownItem>
                                <StyledLink to="/entrar">Entrar</StyledLink>
                            </DropdownItem>
                            <DropdownItem>
                                <StyledLink to="/cadastrar">Cadastrar</StyledLink>
                            </DropdownItem>

                        </Dropdown>
                    )}
                </Icone>
            </Icones>
        </Container>
    );
}

const Container = styled.div`
    position: relative;
    
`;

const SearchBox = styled.div`
  position: absolute;
  top: 30px;
  right: 0;
  background-color: #e0e0e0;
  border: 2px solid #000;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
  padding: 6px;
  z-index: 99;

  input {
    font-family: 'Press Start 2P', monospace;
    font-size: 12px;
    padding: 4px;
    width: 160px;
    border: 2px solid #000;
    background-color: #fff;
    box-shadow: inset -1px -1px #fff, inset 1px 1px #808080;
    outline: none;
  }
`;

const NavWindow = styled.div`
    width: 100%;
    border: 2px solid #000;
    background-color: #e0e0e0;
    box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
    font-family: 'Press Start 2P', monospace;
    padding: 20px 0;
    box-sizing: border-box;
`;

const Icones = styled.ul`
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 20px;
    padding-left: 6px;
`;

const Icone = styled.li`
    position: relative;
    display: flex;
    cursor: pointer;
    list-style-type: none;

    img {
        width: 20px;
        height: 20px;
    }
`;

const Dropdown = styled.ul`
    position: absolute;
    top: 30px;
    right: 0;
    background-color: #fff;
    border: 2px solid #000;
    box-shadow: 2px 2px #808080;
    list-style: none;
    padding: 10px;
    z-index: 100;
`;

const DropdownItem = styled.li`
    font-family: 'Press Start 2P', monospace;
    font-size: 14px;
    padding: 5px 10px;
    color: purple;
    cursor: pointer;

    &:hover {
        background-color: #f0f0f0;
    }
`;

const StyledLink = styled(Link)`
  text-decoration: none;
  color: purple;
  display: block;
  font-family: 'Press Start 2P', monospace;
  font-size: 14px;

  &:hover {
    background-color: #f0f0f0;
  }
`;


export default IconesNavBar;
