import React, { useState } from "react";
import styled from "styled-components";

function Categorias() {
  const [categorias, setCategorias] = useState([
    "Inspiração",
    "Leituras",
    "Games",
    "Filmes",
    "Séries",
    "Animes",
    "Mangá",
    "Reflexões",
    "Ideias Soltas",
    "Criatividade",
    "DevLogs",
    "Pessoal"
  ]);

  const [novaCategoria, setNovaCategoria] = useState("");

  const adicionarCategoria = () => {
    if (novaCategoria.trim()) {
      setCategorias([...categorias, novaCategoria]);
      setNovaCategoria("");
    }
  };

  return (
    <XPWindow>
      <TitleBar>
        <h2>Categorias do Blog</h2>
        <InputArea>
          <input
            type="text"
            placeholder="Nova categoria"
            value={novaCategoria}
            onChange={(e) => setNovaCategoria(e.target.value)}
          />
          <StyledButton onClick={adicionarCategoria}>Criar</StyledButton>
        </InputArea>
      </TitleBar>

      <CategoriaLista>
        {categorias.map((cat, index) => (
          <CategoriaItem key={index}>{cat}</CategoriaItem>
        ))}
      </CategoriaLista>
    </XPWindow>
  );
}

const XPWindow = styled.div`
  max-width: 700px;
  margin: 40px auto;
  border: 2px solid #000;
  background-color: #e0e0e0;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
  font-family: 'Press Start 2P', monospace;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const TitleBar = styled.div`
  background-color: purple;
  color: white;
  padding: 20px;
  font-size: 20px;
  border-bottom: 2px solid #000;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 15px;

  h2 {
    margin: 0;
    font-size: 16px;
    text-align: center;
  }
`;

const InputArea = styled.div`
  display: flex;
  gap: 10px;
  width: 100%;
  justify-content: center;

  input {
    padding: 8px;
    width: 250px;
    font-family: 'Press Start 2P', monospace;
    font-size: 10px;
    border: 2px solid #000;
    background-color: #fff;
  }
`;

const StyledButton = styled.button`
  font-family: 'Press Start 2P', monospace;
  font-size: 10px;
  padding: 10px 14px;
  background-color: #fff;
  color: purple;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  cursor: pointer;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const CategoriaLista = styled.div`
  margin-top: 30px;
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  justify-content: center;
`;

const CategoriaItem = styled.div`
  background-color: #fff;
  padding: 8px 12px;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  font-size: 10px;
  color: #000;
`;

export default Categorias;
