import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [usuario, setUsuario] = useState(null);

  // Na inicialização do app, tenta recuperar o usuário do localStorage
  useEffect(() => {
    const usuarioStorage = localStorage.getItem('usuario');
    if (usuarioStorage) {
      setUsuario(JSON.parse(usuarioStorage));
    }
  }, []);

  // Função de login, recebe dados do login (token + usuário)
  const login = (dados) => {
    setUsuario(dados.usuario);
    localStorage.setItem('usuario', JSON.stringify(dados.usuario));
    localStorage.setItem('token', dados.token);
  };

  // Função de logout limpa o estado e localStorage
  const logout = () => {
    setUsuario(null);
    localStorage.removeItem('usuario');
    localStorage.removeItem('token');
  };

  return (
    <AuthContext.Provider value={{ usuario, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

// Hook para consumir o contexto facilmente
export function useAuth() {
  return useContext(AuthContext);
}
