import styled from 'styled-components';

function Perfil({ usuario, humorEmoji, onEditar }) {
  // Foto padr√£o caso n√£o tenha
  const fotoPadrao = 'https://i.pravatar.cc/150?img=65';

  if (!usuario) {
    return <PerfilContainer>Carregando perfil...</PerfilContainer>;
  }
<Perfil
  usuario={{
    fotoURL: '', // pode ser vazio para usar a padr√£o
    nomeExibicao: 'Ana Maria',
    username: 'ana123',
    bio: 'Sou uma pessoa feliz e amo programar!'
  }}
  humorEmoji="üòÑ"
  onEditar={() => alert('Abrir tela de editar perfil')}
/>

  return (
    <PerfilContainer>
      <LadoEsquerdo>
        <FotoContainer>
          <Foto src={usuario.fotoURL || fotoPadrao} alt={`${usuario.nomeExibicao} foto`} />
          <HumorBalao>{humorEmoji}</HumorBalao>
        </FotoContainer>

        <NomeContainer>
          <NomeExibicao>{usuario.nomeExibicao}</NomeExibicao>
          <Username>@{usuario.username}</Username>
        </NomeContainer>
      </LadoEsquerdo>

      <LadoDireito>
        <BioContainer>
          <BioText>{usuario.bio || 'Sem bio cadastrada.'}</BioText>
          <EditarBotao onClick={onEditar}>Editar Perfil</EditarBotao>
        </BioContainer>
      </LadoDireito>
    </PerfilContainer>
  );
}

/* Styled Components */

const PerfilContainer = styled.div`
  display: flex;
  gap: 30px;
  background-color: #e0e0e0;
  border: 2px solid #000;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
  padding: 20px;
  font-family: 'Press Start 2P', monospace;
  max-width: 600px;
`;

const LadoEsquerdo = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 180px;
`;

const FotoContainer = styled.div`
  position: relative;
  width: 120px;
  height: 120px;
`;

const Foto = styled.img`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  border: 2px solid #000;
  box-shadow: inset -1px -1px #fff, inset 1px 1px #808080;
  object-fit: cover;
`;

const HumorBalao = styled.div`
  position: absolute;
  top: -15px;
  left: 50%;
  transform: translateX(-50%);
  background: #fff;
  border: 2px solid #000;
  border-radius: 12px;
  padding: 4px 8px;
  font-size: 26px;
  box-shadow: inset -1px -1px #fff, inset 1px 1px #808080;
`;

const NomeContainer = styled.div`
  margin-top: 12px;
  text-align: center;
`;

const NomeExibicao = styled.div`
  font-size: 14px;
  color: purple;
  font-weight: bold;
`;

const Username = styled.div`
  font-size: 10px;
  color: #555;
`;

const LadoDireito = styled.div`
  flex-grow: 1;
`;

const BioContainer = styled.div`
  background: #fff;
  border: 2px solid #000;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
  padding: 15px;
  max-height: 120px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const BioText = styled.p`
  font-size: 12px;
  color: #333;
  margin-bottom: 15px;
  white-space: pre-wrap;
  overflow-y: auto;
`;

const EditarBotao = styled.button`
  align-self: flex-end;
  background-color: purple;
  color: white;
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 6px 12px;
  border: none;
  cursor: pointer;
  box-shadow: inset -1px -1px #fff, inset 1px 1px #808080;

  &:hover {
    background-color: #a020a0;
  }
`;

export default Perfil;
