import React from "react";
import styled from "styled-components";
import Head from '../Head';

const MainPage = ({ termoBusca, categorias, posts }) => {
    console.log("游댌 Termo de busca:", termoBusca);
    console.log("游늭 Categorias:", categorias);
    console.log("游닇 Posts:", posts);

    // Filtrar categorias e posts conforme a busca
    const resultados = [...new Set([...categorias, ...posts].map(item => JSON.stringify(item)))].map(str => JSON.parse(str)).filter(item => item.nome.toLowerCase().includes(termoBusca.toLowerCase()));

    console.log("游꿢 Resultados filtrados:", resultados);

    return (
        <MainContainer>
            <Head />
             

            {/* S칩 mostra a busca quando h치 termo digitado */}
            {termoBusca && (
                <ResultadosContainer>
                    {resultados.length === 0 ? (
                        <p>Nenhum resultado encontrado.</p>
                    ) : (
                        resultados.map((item, index) => (
                            <Resultado key={`${item.id}-${index}`}>{item.nome}</Resultado>
                        ))
                    )}
                </ResultadosContainer>
            )}

            <Separator />  
        </MainContainer>
    );
};

const MainContainer = styled.section`
  padding: 20px;
`;

const Resultado = styled.div`
  padding: 10px;
  border-bottom: 1px solid #ccc;
`;

const ResultadosContainer = styled.section`
  margin-top: 20px; 
  padding: 10px;
  border-top: 2px solid #ccc;
`;



const Separator = styled.div`
    width: 100%;
    height: 60px; 
    background: #666; /* Cor do ret칙ngulo separador */
`;

export default MainPage;
