import React, { useState, } from 'react';
import styled from 'styled-components';
import { useAuth } from '../Contexts/AuthContext';
import { FaEdit } from 'react-icons/fa';
import { emojis } from '../Componentes/Utils/emojisLista';



const Perfil = () => {
  const { usuario } = useAuth();

  // Carregar emoji salvo ou usar padrÃ£o
  const [emoji, setEmoji] = useState(() => {
    return localStorage.getItem('perfilEmoji') || 'ðŸ˜€';
  });

  const [editando, setEditando] = useState(false);
  const [mostrarDropdown, setMostrarDropdown] = useState(false);
  const [fotoNome, setFotoNome] = useState('');

  // Carregar bio salva ou do usuÃ¡rio
  const [formData, setFormData] = useState(() => {
    const savedBio = localStorage.getItem('perfilBio');
    return {
      nome_exibicao: usuario?.nome_exibicao || '',
      email: usuario?.email || '',
      senha: '',
      bio: savedBio || usuario?.biografia || '',
      foto: usuario?.foto || `https://robohash.org/${usuario?.username}.png?set=cat`,
    };
  });

  if (!usuario) return <p>VocÃª precisa estar logada para acessar o perfil.</p>;

  // Salvar emoji no localStorage sempre que mudar
  useEffect(() => {
    localStorage.setItem('perfilEmoji', emoji);
  }, [emoji]);

  // Salvar bio no localStorage sempre que mudar
  useEffect(() => {
    localStorage.setItem('perfilBio', formData.bio);
  }, [formData.bio]);

  const handleInputChange = (e) => {
    setFormData((f) => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleEmojiClick = (emj) => {
    setEmoji(emj);
    setMostrarDropdown(false);
  };

  const handleFotoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFotoNome(file.name);
      // upload real depois, se quiser
    }
  };

  const salvarEdicao = (e) => {
    e.preventDefault();
    console.log('Salvando dados:', formData);
    setEditando(false);
  };

    
  return (
    <PerfilContainer>
      <Titulo>Zona terrestre de @{usuario.username}</Titulo>

      <Container>
        <ProfileBox>
          <LeftSide>
            <UserPhoto src={fotoNome ? URL.createObjectURL(fotoNome) : (usuario.foto || defaultPhotoUrl)} alt="Foto de perfil" />
            <EmojiBadge onClick={() => setMostrarDropdown((prev) => !prev)}>
              {emoji}
              {mostrarDropdown && (
                <Dropdown>
                  {emojis.map((emj, i) => (
                    <DropdownItem key={i} onClick={() => handleEmojiClick(emj)}>
                      {emj}
                    </DropdownItem>
                  ))}
                </Dropdown>
              )}
            </EmojiBadge>
            <DisplayName>{usuario.nome_exibicao}</DisplayName>
            <Username>@{usuario.username}</Username>
          </LeftSide>

          <RightSide>
            <Bio>{usuario.biografia}</Bio>

            

            <StyledButton onClick={() => setEditando(true)}>
              <FaEdit /> Editar perfil
            </StyledButton>
          </RightSide>
        </ProfileBox>
      </Container>

      {editando && (
        <BlurOverlay>
          <PopupWindow>
            <PopupHeader>
              Editar perfil
              <PopupButtons>
                <button onClick={() => setEditando(false)}>X</button>
              </PopupButtons>
            </PopupHeader>

            <form onSubmit={salvarEdicao}>
              <PopupContent>
                <label>Nome de exibiÃ§Ã£o</label>
                <input name="nome_exibicao" value={formData.nome_exibicao} onChange={handleInputChange} />

                <label>Email</label>
                <input name="email" value={formData.email} onChange={handleInputChange} />

                <label>Senha (nova)</label>
                <input type="password" name="senha" value={formData.senha} onChange={handleInputChange} />

                <label>Bio</label>
                <textarea name="bio" value={formData.bio} onChange={handleInputChange} />

                <ImageUploadContainer>
                  <label htmlFor="upload">ðŸ“· Trocar foto</label>
                  <input type="file" id="upload" accept="image/*" onChange={handleFotoUpload} />
                  {fotoNome && <NomeArquivo>{fotoNome}</NomeArquivo>}
                </ImageUploadContainer>

                <StyledButton type="submit">Salvar</StyledButton>
              </PopupContent>
            </form>
          </PopupWindow>
        </BlurOverlay>
      )}
    </PerfilContainer>
  );
};

export default Perfil;

// ======== STYLED COMPONENTS =========

const PerfilContainer = styled.div`
  background: linear-gradient(135deg, #d4d4ff, #f0e0ff);
  min-height: 100vh;
  padding: 40px 20px; /* menos nas laterais pra nÃ£o ficar esmagado */
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Titulo = styled.h1`
  font-size: 16px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 3px 3px #808080;
  padding: 10px 20px;
  margin-bottom: 30px;
  color: purple;
  font-family: 'Press Start 2P', monospace;
  text-align: center;
  width: fit-content; /* sÃ³ ocupa o tamanho do texto */
`;

const Container = styled.div`
  width: 100%;
  max-width: 1000px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 4px 4px #808080;
  padding: 30px 40px;
  display: flex;
  flex-direction: row;
  gap: 40px;

