import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [usuario, setUsuario] = useState(null);

  useEffect(() => {
    // Carregar dados do localStorage ao montar o componente
    const userStorage = localStorage.getItem('usuario');
    if (userStorage) {
      setUsuario(JSON.parse(userStorage));
    }
  }, []);

  function login(dados) {
  const { token, usuario } = dados;

  localStorage.setItem('token', token);
  localStorage.setItem('usuario', JSON.stringify(usuario)); // <-- isso
  setUsuario(usuario); // <-- isso também
}

  function logout() {
    setUsuario(null);
    localStorage.removeItem('token');
    localStorage.removeItem('usuario'); // <-- isso aqui é importante
    localStorage.removeItem('perfilEmoji'); // se estiver usando emojis salvos
    localStorage.removeItem('perfilBio');   // idem
  }

  return (
    <AuthContext.Provider value={{ usuario, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  return useContext(AuthContext);
}
