import styled from "styled-components";
import Input from  "../Input";
import { useState } from "react";
import { categorias } from './dadosPesquisa'


function Pesquisa() {
    const [categoriasPesquisadas, setcategoriasPesquisadas] = useState([])

    console.log(categoriasPesquisadas)
    return (
        <PesquisaContainer>
            <Input
                placeholder="Procurando algum tema?"
                onBlur={evento =>  {
                    const textoDigitado = evento.target.value
                    const resultadoPesquisa = categorias.filter(post => post.nome.includes(textoDigitado))
                    setcategoriasPesquisadas(resultadoPesquisa)
                }}
            />
           
        </PesquisaContainer>
    )
}


const PesquisaContainer = styled.section`

`

export default Pesquisa
