import React, { useState } from "react";
import styled from "styled-components";
import Categorias from "./Categorias";

function Postagens() {
  const [categoriaSelecionada, setCategoriaSelecionada] = useState(null);

  // Dados mock (vai trocar pelo fetch do back depois)
  const MOCK_POSTAGENS = [
    {
      id: 1,
      titulo: "Primeiro post",
      conteudo: "Esse é o conteúdo do post de exemplo.",
      post_imagem: "https://via.placeholder.com/150",
      categorias_id: "Inspiração",
      usuarios_id: 1,
      moods_id: 2,
    },
    {
      id: 2,
      titulo: "Reflexão do dia",
      conteudo: "Hoje foi um dia muito reflexivo...",
      post_imagem: "https://via.placeholder.com/150",
      categorias_id: "Reflexões",
      usuarios_id: 2,
      moods_id: 3,
    },
    {
      id: 3,
      titulo: "Gameplay top",
      conteudo: "Review do último jogo que joguei.",
      post_imagem: "https://via.placeholder.com/150",
      categorias_id: "Games",
      usuarios_id: 3,
      moods_id: 1,
    },
  ];

  // Filtra posts pela categoria selecionada
  const postsFiltrados = categoriaSelecionada
    ? MOCK_POSTAGENS.filter((post) => post.categorias_id === categoriaSelecionada)
    : [];

  return (
    <PageWrapper>
      <TituloBlog>📝 Postagens no Blog</TituloBlog>
      <Container>
        <Categorias onCategoriaClick={setCategoriaSelecionada} />

        <XPWindow>
          <TitleBar>
            <h2>Crie uma nova postagem</h2>
            <StyledButton>Criar</StyledButton>
          </TitleBar>
          {/* Formulário para criar post pode ir aqui depois */}
        </XPWindow>
      </Container>

      {/* Seção separada para mostrar os posts filtrados */}
      {categoriaSelecionada && (
        <PostsSection>
          <FiltroInfo>
            Posts da categoria: <strong>{categoriaSelecionada}</strong>
          </FiltroInfo>

          <PostsContainer>
            {postsFiltrados.length > 0 ? (
              postsFiltrados.map((post) => (
                <PostCard key={post.id}>
                  <PostImagem src={post.post_imagem} alt={post.titulo} />
                  <PostConteudo>
                    <PostTitulo>{post.titulo}</PostTitulo>
                    <PostTexto>{post.conteudo}</PostTexto>
                    <PostDetalhes>
                      Categoria: {post.categorias_id} | Usuário: {post.usuarios_id} | Se sentindo: {post.moods_id}
                    </PostDetalhes>
                  </PostConteudo>
                </PostCard>
              ))
            ) : (
              <SemPosts>Nenhum post nessa categoria.</SemPosts>
            )}
          </PostsContainer>
        </PostsSection>
      )}
    </PageWrapper>
  );
}

// Styled components (continua igual, só adiciona os que faltavam)

const PageWrapper = styled.div`
  background: linear-gradient(135deg, #d4d4ff, #f0e0ff);
  min-height: 100vh;
  padding: 40px 20px;
  font-family: 'Press Start 2P', monospace;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TituloBlog = styled.h1`
  font-size: 16px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 3px 3px #808080;
  padding: 10px 20px;
  margin-bottom: 30px;
  color: purple;
`;

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: flex-start;
  gap: 20px;
  flex-wrap: wrap;
`;

const XPWindow = styled.div`
  max-width: 600px;
  border: 2px solid #000;
  border-radius: 6px;
  background-color: #e0e0e0;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080, 3px 3px #999;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const TitleBar = styled.div`
  background-color: purple;
  color: white;
  padding: 20px;
  font-size: 20px;
  border-bottom: 2px solid #000;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;

  h2 {
    margin: 0;
    font-size: 14px;
    text-align: center;
  }
`;

const StyledButton = styled.button`
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 10px 20px;
  background-color: #fff;
  color: purple;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  cursor: pointer;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const FiltroInfo = styled.div`
  font-size: 12px;
  margin-bottom: 15px;
  color: purple;
  font-weight: bold;
`;

const PostsSection = styled.section`
  margin-top: 40px;
  max-width: 700px;
  width: 100%;
  font-family: 'Press Start 2P', monospace;
`;

const PostsContainer = styled.div`
  margin-top: 10px;
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const PostCard = styled.div`
  display: flex;
  gap: 15px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  padding: 15px;
  border-radius: 6px;
`;

const PostImagem = styled.img`
  width: 120px;
  height: 120px;
  object-fit: cover;
  border: 2px solid #000;
  border-radius: 6px;
`;

const PostConteudo = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const PostTitulo = styled.h3`
  margin: 0;
  font-size: 14px;
  color: purple;
`;

const PostTexto = styled.p`
  margin: 8px 0 12px 0;
  font-size: 10px;
  color: #222;
`;

const PostDetalhes = styled.div`
  font-size: 8px;
  color: #666;
`;

const SemPosts = styled.div`
  font-size: 12px;
  color: purple;
  font-weight: bold;
  text-align: center;
  margin-top: 20px;
`;

export default Postagens;
