import styled from "styled-components";

const textoOpcoes = ['Home', 'Postagens', 'Categorias', 'Ideias', 'Sobre'];

function NavBar() {
    return (
        <NavContainer>
            <NavWrapper>
                <Spacer /> {/* Espaço esquerdo */}
                <Opcoes>
                    {textoOpcoes.map((texto) => (
                        <Opcao key={texto}><p>{texto}</p></Opcao>
                    ))}
                </Opcoes>
                <Spacer /> {/* Espaço direito */}
            </NavWrapper>
        </NavContainer>
    )
}

const NavContainer = styled.nav`
    width: 100%;
    display: flex;
    justify-content: center;
    background: white;
`;

const NavWrapper = styled.div`
    display: flex;
    align-items: center;
    width: 100%;
    max-width: 1200px; /* Define um limite máximo */
    border: 2px solid black;
`;

const Opcoes = styled.ul`
    display: flex;
    justify-content: space-around;
    align-items: center;
    gap: 5px;
    flex-grow: 1;
    list-style: none;
`;

const Opcao = styled.li`
    color: #2d2d34;
    font-size: 18px;
    cursor: pointer;
    text-align: center;
    padding: 30px 15px;
    box-sizing: border-box;

    &:hover {
        color: rgb(101, 101, 116);
    }
`;

const Spacer = styled.div`
    flex: 1; /* Ocupa o espaço extra nas laterais */
`;

export default NavBar;
