import styled from 'styled-components';
import { useAuth } from '../Contexts/AuthContext';
import React, { useState, useEffect } from 'react';


const Perfil = () => {
  const { usuario } = useAuth();

  // Inicializa emoji e bio com o que tiver no localStorage ou do usuário
  const [emoji, setEmoji] = useState(() => localStorage.getItem('perfilEmoji') || '😀');
  const [editando, setEditando] = useState(false);
  const [mostrarDropdown, setMostrarDropdown] = useState(false);
  const [fotoNome, setFotoNome] = useState('');

  const [formData, setFormData] = useState(() => {
    const savedBio = localStorage.getItem('perfilBio');
    return {
      nome_exibicao: usuario?.nome_exibicao || '',
      email: usuario?.email || '',
      senha: '',
      bio: savedBio || usuario?.biografia || '',
      foto: usuario?.foto || `https://robohash.org/${usuario?.username}.png?set=cat`,
    };
  });

  // Hooks devem vir aqui, antes do return
  useEffect(() => {
    localStorage.setItem('perfilEmoji', emoji);
  }, [emoji]);

  useEffect(() => {
    localStorage.setItem('perfilBio', formData.bio);
  }, [formData.bio]);

  if (!usuario) return <p>Você precisa estar logada para acessar o perfil.</p>;

  // Handlers
  const handleInputChange = (e) => {
    setFormData(f => ({ ...f, [e.target.name]: e.target.value }));
  };

  const handleEmojiClick = (emj) => {
    setEmoji(emj);
    setMostrarDropdown(false);
  };

  const handleFotoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFotoNome(file.name);
      // Aqui só guardamos o nome, upload real precisa deploy/backend
      // Se quiser, pode gerar uma URL temporária para preview:
      const url = URL.createObjectURL(file);
      setFormData(f => ({ ...f, foto: url }));
    }
  };

  const salvarEdicao = (e) => {
    e.preventDefault();
    // Aqui você faria a chamada para salvar no backend
    console.log('Salvando dados:', { emoji, ...formData });
    setEditando(false);
    alert('Perfil salvo (simulado)');
  };

  return (
    <div>
      <h2>Perfil de {usuario.nome_exibicao}</h2>

      <div>
        <strong>Emoji:</strong> <span style={{ fontSize: '2rem' }}>{emoji}</span>{' '}
        <button onClick={() => setMostrarDropdown(!mostrarDropdown)}>
          Alterar Emoji
        </button>
        {mostrarDropdown && (
          <div style={{ border: '1px solid #ccc', padding: 10, maxWidth: 200 }}>
            {['😀', '😎', '😍', '😢', '😡'].map(emj => (
              <button
                key={emj}
                onClick={() => handleEmojiClick(emj)}
                style={{ fontSize: '1.5rem', margin: 5 }}
              >
                {emj}
              </button>
            ))}
          </div>
        )}
      </div>

      <form onSubmit={salvarEdicao}>
        <div>
          <label>
            Nome de Exibição:
            <input
              type="text"
              name="nome_exibicao"
              value={formData.nome_exibicao}
              onChange={handleInputChange}
              disabled={!editando}
            />
          </label>
        </div>

        <div>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              disabled={!editando}
            />
          </label>
        </div>

        <div>
          <label>
            Biografia:
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleInputChange}
              disabled={!editando}
            />
          </label>
        </div>

        <div>
          <label>
            Foto de Perfil:
            <input type="file" onChange={handleFotoUpload} disabled={!editando} />
          </label>
          {fotoNome && <p>Arquivo selecionado: {fotoNome}</p>}
          {formData.foto && (
            <img
              src={formData.foto}
              alt="Foto de perfil"
              style={{ width: 150, height: 150, objectFit: 'cover', borderRadius: '50%' }}
            />
          )}
        </div>

        <div style={{ marginTop: 15 }}>
          {!editando && (
            <button type="button" onClick={() => setEditando(true)}>
              Editar Perfil
            </button>
          )}
          {editando && (
            <>
              <button type="submit">Salvar</button>
              <button type="button" onClick={() => setEditando(false)}>
                Cancelar
              </button>
            </>
          )}
        </div>
      </form>
    </div>
  );
};



export default Perfil;

// ======== STYLED COMPONENTS =========

const PerfilContainer = styled.div`
  background: linear-gradient(135deg, #d4d4ff, #f0e0ff);
  min-height: 100vh;
  padding: 40px 20px; /* menos nas laterais pra não ficar esmagado */
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Titulo = styled.h1`
  font-size: 16px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 3px 3px #808080;
  padding: 10px 20px;
  margin-bottom: 30px;
  color: purple;
  font-family: 'Press Start 2P', monospace;
  text-align: center;
  width: fit-content; /* só ocupa o tamanho do texto */
`;

const Container = styled.div`
  width: 100%;
  max-width: 1000px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 4px 4px #808080;
  padding: 30px 40px;
  display: flex;
  flex-direction: row;
  gap: 40px;

  @media (max-width: 768px) {
    flex-direction: column;
    padding: 20px;
  }
`;

const ProfileBox = styled.div`
  display: flex;
  gap: 40px;
  align-items: flex-start;
  width: 100%;

  @media (max-width: 768px) {
    flex-direction: column;
    align-items: center;
  }
`;

const LeftSide = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
`;

const UserPhoto = styled.img`
  width: 150px;
  height: 150px;
  border: 3px solid #000;
  box-shadow: 4px 4px #555;
  object-fit: cover;
  background-color: #fff;
`;

const EmojiBadge = styled.div`
  font-size: 30px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  padding: 5px 10px;
  margin-top: -20px;
  margin-bottom: 10px;
  cursor: pointer;
  position: relative;
  user-select: none;
`;

const DisplayName = styled.p`
  font-size: 24px;
  margin: 5px 0;
  color: purple;
  font-weight: bold;
`;

const Username = styled.p`
  font-size: 16px;
  color: #555;
  margin: 0;
`;

const RightSide = styled.div`
  flex: 2;
  display: flex;
  flex-direction: column;
`;

const Bio = styled.p`
  font-size: 16px;
  color: #333;
  margin-bottom: 20px;
  white-space: pre-wrap;
`;

const Redes = styled.div`
  font-size: 12px;
  color: purple;
  margin-bottom: 20px;

  p {
    margin-bottom: 6px;
  }
`;

const StyledButton = styled.button`
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 10px;
  background-color: #fff;
  color: purple;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  cursor: pointer;
  width: 150px;
  align-self: flex-end;
 

  display: flex;
  align-items: center;
  gap: 5px;

  &:hover {
    background-color: #f0f0f0;
  }
`;

const BlurOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
