import React from "react";
import styled from "styled-components";
import cover from "../../Assets/selfparking-v3.png";
import { XPButton } from '../XPButton';



const HeadMain = () => {
    return (
        <HeadContainer>

            <Content>
                <h1>Olá Terráqueos</h1>
                <p>Explore e compartilhe ideias incríveis!</p>
                <XPButton>Login</XPButton>
                
            </Content>
            <Image src={cover} alt="Imagem da página principal" />
        </HeadContainer>
    );
}

const HeadContainer = styled.section`
    display: flex;
    align-items: center;
    justify-content: flex-end;
    flex-wrap: wrap;
    padding: 40px 0;
    width: 100%;
    max-width: 100vw;
    box-sizing: border-box;
   
    overflow-x: hidden;
    text-align: center;

    @media (max-width: 768px) {
        flex-direction: column;
        gap: 20px;
    }
`;

const Image = styled.img`
    flex: 1;
   
    max-width: 60%;
    height: auto;
    object-fit: contain;
    display: block;

    @media (max-width: 768px) {
        max-width: 100%;
        width: 100%;
    }
`;


const Content = styled.div`
   display: flex;
  max-width: 40%;
  flex-direction: column;
  justify-content: center;
  align-items: center; /* centraliza tudo horizontalmente */
  padding: 20px;
  color: #333;
  max-width: 500px;
  text-align: center; /* centraliza texto também */

    h1 {
        font-size: 48px;
        margin-bottom: 10px;
    }

    p {
        font-size: 22px;
    }

    @media (max-width: 768px) {
        align-items: center;
    }
`;



export default HeadMain;
