import styled from 'styled-components';

<Perfil 
  usuario={{
    fotoURL: 'https://i.pravatar.cc/150?img=5',
    nomeExibicao: 'Ana Maria',
    username: 'ana123',
    bio: 'Sou uma pessoa feliz e amo programar!'
  }}
  humorEmoji="üòÑ"
  onEditar={() => alert('Abrir tela de editar perfil')}
/>


function Perfil({ usuario, humorEmoji, onEditar }) {
  return (
    <PerfilContainer>
      <FotoContainer>
        <Foto src={usuario.fotoURL} alt={`${usuario.nome} foto`} />
        <HumorBal√£o>{humorEmoji}</HumorBal√£o>
        <NomeContainer>
          <NomeExibicao>{usuario.nomeExibicao}</NomeExibicao>
          <Username>@{usuario.username}</Username>
        </NomeContainer>
      </FotoContainer>

      <BioContainer>
        <BioText>{usuario.bio || "Sem bio cadastrada."}</BioText>
        <EditarBotao onClick={onEditar}>Editar Perfil</EditarBotao>
      </BioContainer>
    </PerfilContainer>
  );
}

/* Styled Components */

const PerfilContainer = styled.div`
  display: flex;
  gap: 30px;
  background-color: #e0e0e0;
  border: 2px solid #000;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
  padding: 20px;
  font-family: 'Press Start 2P', monospace;
`;

const FotoContainer = styled.div`
  position: relative;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 150px;
`;

const Foto = styled.img`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  border: 2px solid #000;
  box-shadow: inset -1px -1px #fff, inset 1px 1px #808080;
`;

const HumorBal√£o = styled.div`
  position: absolute;
  top: -15px;
  left: 50%;
  transform: translateX(-50%);
  background: #fff;
  border: 2px solid #000;
  border-radius: 12px;
  padding: 4px 8px;
  font-size: 24px;
  box-shadow: inset -1px -1px #fff, inset 1px 1px #808080;
`;

const NomeContainer = styled.div`
  margin-top: 12px;
  text-align: center;
`;

const NomeExibicao = styled.div`
  font-size: 14px;
  color: purple;
`;

const Username = styled.div`
  font-size: 10px;
  color: #555;
`;

const BioContainer = styled.div`
  flex-grow: 1;
  background: #fff;
  border: 2px solid #000;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
  padding: 15px;
  max-height: 120px;
  overflow-y: auto;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const BioText = styled.p`
  font-size: 12px;
  color: #333;
  margin-bottom: 15px;
  white-space: pre-wrap; /* mant√©m quebras de linha */
`;

const EditarBotao = styled.button`
  align-self: flex-end;
  background-color: purple;
  color: white;
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 6px 12px;
  border: none;
  cursor: pointer;
  box-shadow: inset -1px -1px #fff, inset 1px 1px #808080;

  &:hover {
    background-color: #a020a0;
  }
`;

export default Perfil;
