import React from "react";
import styled from "styled-components";

const XPWindow = styled.div`
  max-width: 600px; /* Tamanho máximo do quadrado */
  margin: 20px auto; /* Centraliza horizontalmente */
  border: 2px solid #000;
  background-color: #e0e0e0;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080;
  font-family: 'Press Start 2P', monospace;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const TitleBar = styled.div`
  background-color: purple;
  color: white;
  padding: 10px;
  font-size: 20px;
  border-bottom: 2px solid #000;
  width: 100%; /* Faz com que o TitleBar ocupe toda a largura do XPWindow */
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Content = styled.div`
  padding: 20px;
  color: #000;
  width: 100%; /* Faz com que o Content ocupe toda a largura do XPWindow */

  h2 {
    margin-top: 20px;
    font-size: 18px;
  }

  p {
    font-size: 16px;
    line-height: 1.8;
    margin-top: 10px;
  }

  ul {
    font-family: 'Press Start 2P', monospace;
    font-size: 16px;
    list-style-type: disc;
    padding-left: 20px;
  }

  a {
    color: purple;
    text-decoration: none;
  }

  a:hover {
    text-decoration: underline;
  }
`;
