import React, { useEffect, useState, useContext } from "react";
import {
  Container,
  XPWindow,
  Header,
  TituloCategoria,
  PostCard,
  PostTitulo,
  PostConteudo,
  PostDetalhes,
  CategoriaButton,
  CriarPostButton,
  PostForm,
  Input,
  TextArea,
  Select,
  BlurBackground,
} from "./PostagensStyles";
import axios from "axios";
import { AuthContext } from "../../contexts/AuthContext";

const Postagens = () => {
  const [posts, setPosts] = useState([]);
  const [categoriaSelecionada, setCategoriaSelecionada] = useState("");
  const [mostrarForm, setMostrarForm] = useState(false);
  const [titulo, setTitulo] = useState("");
  const [conteudo, setConteudo] = useState("");
  const [humorSelecionado, setHumorSelecionado] = useState("");

  const { currentUser } = useContext(AuthContext);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const res = await axios.get("http://localhost:3001/posts");
        setPosts(res.data);
      } catch (err) {
        console.error("Erro ao buscar posts:", err);
      }
    };

    fetchPosts();
  }, []);

  const handleCategoriaClick = (categoria) => {
    setCategoriaSelecionada(categoria === categoriaSelecionada ? "" : categoria);
  };

  const handlePostSubmit = async () => {
    if (!currentUser) {
      alert("VocÃª precisa estar logada para criar um post.");
      return;
    }

    if (!titulo || !conteudo || !categoriaSelecionada || !humorSelecionado) {
      alert("Preencha todos os campos!");
      return;
    }

    try {
      await axios.post("http://localhost:3001/posts", {
        titulo,
        conteudo,
        categoria: categoriaSelecionada,
        usuarioId: currentUser.id,
        humorId: humorSelecionado,
      });

      // Atualizar lista de posts apÃ³s criaÃ§Ã£o
      const res = await axios.get("http://localhost:3001/posts");
      setPosts(res.data);

      // Resetar form
      setTitulo("");
      setConteudo("");
      setCategoriaSelecionada("");
      setHumorSelecionado("");
      setMostrarForm(false);
    } catch (err) {
      console.error("Erro ao criar post:", err);
    }
  };

  const categorias = [...new Set(posts.map((post) => post.categoria))];

  const postsFiltrados = categoriaSelecionada
    ? posts.filter((post) => post.categoria === categoriaSelecionada)
    : posts;

  return (
    <Container>
      <Header>
        {categorias.map((categoria, index) => (
          <CategoriaButton
            key={index}
            onClick={() => handleCategoriaClick(categoria)}
            ativo={categoria === categoriaSelecionada}
          >
            {categoria}
          </CategoriaButton>
        ))}

        <CriarPostButton onClick={() => {
          if (!currentUser) {
            alert("VocÃª precisa estar logada para criar um post!");
            return;
          }
          setMostrarForm(true);
        }}>
          Criar Post
        </CriarPostButton>
      </Header>

      {mostrarForm && (
        <BlurBackground>
          <XPWindow>
            <PostForm>
              <Input
                placeholder="TÃ­tulo"
                value={titulo}
                onChange={(e) => setTitulo(e.target.value)}
              />
              <TextArea
                placeholder="ConteÃºdo"
                value={conteudo}
                onChange={(e) => setConteudo(e.target.value)}
              />
              <Select
                value={categoriaSelecionada}
                onChange={(e) => setCategoriaSelecionada(e.target.value)}
              >
                <option value="">Selecione a categoria</option>
                {categorias.map((categoria, index) => (
                  <option key={index} value={categoria}>
                    {categoria}
                  </option>
                ))}
              </Select>
              <Select
                value={humorSelecionado}
                onChange={(e) => setHumorSelecionado(e.target.value)}
              >
                <option value="">Selecione seu humor</option>
                <option value="1">ðŸ˜Š Feliz</option>
                <option value="2">ðŸ˜¢ Triste</option>
                <option value="3">ðŸ˜  Irritada</option>
                {/* Substitua pelos valores reais do seu banco */}
              </Select>
              <CriarPostButton onClick={handlePostSubmit}>Postar</CriarPostButton>
              <CriarPostButton onClick={() => setMostrarForm(false)}>Cancelar</CriarPostButton>
            </PostForm>
          </XPWindow>
        </BlurBackground>
      )}

      {postsFiltrados.map((post) => (
        <XPWindow key={post.id}>
          <PostCard>
            <PostTitulo>{post.titulo}</PostTitulo>
            <PostConteudo>{post.conteudo}</PostConteudo>
            <PostDetalhes>
              {post.categoria} | Por: {post.usuario?.nome_exibicao} | Se sentindo: {post.humor?.emoji} {post.humor?.descricao}
            </PostDetalhes>
          </PostCard>
        </XPWindow>
      ))}
    </Container>
  );
};

export default Postagens;
