import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import xpwindows from '../Assets/xpwindows.jpg';

function Cadastro() {
  const navigate = useNavigate();
  const [previewImage, setPreviewImage] = useState(null);

const handleImageChange = (e) => {
  const file = e.target.files[0];
  if (file) {
    const imageUrl = URL.createObjectURL(file);
    setPreviewImage(imageUrl);
  }
};
  const handleSubmit = (e) => {
    e.preventDefault();
    alert('Cadastro enviado!');
    //  lógica pra salvar no back
  };

  

  return (
    <BackgroundContainer>
      <GlassForm>
        <TitleBar>
          <Title>Cadastro de Usuário</Title>
          <CloseButton onClick={() => navigate('/')}>X</CloseButton>
        </TitleBar>
        <FormBody>
          <form onSubmit={handleSubmit}>
            <input type="text" placeholder="Nome" required />
            <input type="text" placeholder="Usuário" required />
            <input type="email" placeholder="Email" required />
            <input type="password" placeholder="Senha" required />
            <textarea placeholder="Bio (opcional)" rows={3} />
            <input type="text" placeholder="URL da imagem (opcional)" />

            <button type="submit">Criar Conta</button>
          </form>

          <BackToLogin onClick={() => navigate('/entrar')}>
            Já tem uma conta? <span>Entrar</span>
          </BackToLogin>
        </FormBody>
      </GlassForm>
    </BackgroundContainer>
  );
}

const BackgroundContainer = styled.div`
  position: relative;
  background-image: url(${xpwindows});
  background-size: cover;
  background-position: center;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;

  &::before {
    content: '';
    position: absolute;
    inset: 0;
    background: rgba(0, 0, 0, 0.3);
    z-index: 1;
  }

  > * {
    position: relative;
    z-index: 2;
  }
`;

const GlassForm = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border: 2px solid rgba(255, 255, 255, 0.4);
  box-shadow: 4px 4px 8px rgba(0, 0, 0, 0.4);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  width: 360px;
  border-radius: 6px;
  font-family: 'Press Start 2P', monospace;
  overflow: hidden;
`;

const TitleBar = styled.div`
  background-color: #0a246a;
  color: white;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 10px;
`;

const Title = styled.h2`
  font-size: 14px;
  margin: 0;
`;

const CloseButton = styled.button`
  background: #c0c0c0;
  border: 2px solid #000;
  font-size: 10px;
  font-family: 'Press Start 2P', monospace;
  cursor: pointer;
  padding: 2px 6px;

  &:hover {
    background-color: #a0a0a0;
  }
`;

const FormBody = styled.div`
  padding: 20px;

  form {
    display: flex;
    flex-direction: column;
    gap: 15px;

    input,
    textarea {
      padding: 10px;
      border: 2px solid #000;
      background-color: rgba(255, 255, 255, 0.7);
      font-family: 'Press Start 2P', monospace;
      resize: none;
    }

    button {
      padding: 10px;
      border: 2px solid #000;
      background-color: #c0c0c0;
      cursor: pointer;
      font-family: 'Press Start 2P', monospace;

      &:hover {
        background-color: #a0a0a0;
      }
    }
  }
`;

const BackToLogin = styled.p`
  font-size: 12px;
  text-align: center;
  color: #f1f1f1;
  margin-top: 10px;
  cursor: pointer;

  span {
    text-decoration: underline;
    color: #add8e6;

    &:hover {
      color: #fff;
    }
  }
`;

export default Cadastro;
