import React, { useState } from 'react';
import { Link } from 'react-router-dom';

// Exemplo básico de estado de usuário
function IconesNavBar() {
  // Supondo que usuário seja um objeto com dados quando logado, ou null quando deslogado
  const [usuario, setUsuario] = useState(null);

  const logout = () => {
    setUsuario(null);
    // Pode limpar token, localStorage, etc.
    alert('Logout realizado!');
  };

  return (
    <div>
      {usuario ? (
        <>
          <span>Olá, {usuario.nome}</span>
          <button onClick={logout}>Sair</button>
        </>
      ) : (
        <>
          <Link to="/entrar">Entrar</Link>
          <Link to="/cadastrar">Cadastrar</Link>
        </>
      )}
    </div>
  );
}

export default IconesNavBar;
