import React from "react";
import styled from "styled-components";
import cover from "../../Assets/selfparking.jpg";

const HeadMain = () => {
    return (
        <HeadContainer>
            <Overlay />
            <Content>
                <h1>Ol√° Terr√°queos</h1>
                <p>Explore e compartilhe ideias incr√≠veis!</p>
            </Content>
            <Separator />  {/* üî• Adicionando o separador aqui */}
        </HeadContainer>
    );
}

const HeadContainer = styled.section`
    position: relative;
    width: 100%; /* Largura da tela */
    height: 100vh; 
    background-image: url(${cover});
    background-size: cover; 
    background-position: center;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0 !important;
    
`;

// Adiciona um overlay para dar um efeito mais escuro na imagem de fundo
const Overlay = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5); 
`;

// Conte√∫do dentro do header
const Content = styled.div`
    position: relative;
    color: white;
    text-align: center;
    z-index: 2;
    h1 {
        font-size: 32px;
        margin-bottom: 10px;
    }
    p {
        font-size: 18px;
    }
`;

export default HeadMain;
