import React from "react";
import styled from "styled-components";

const MainPage = ({ termoBusca, categorias, posts }) => {
  // Se o usu√°rio n√£o digitou nada, mostrar mensagem padr√£o

  console.log("üîç Termo de busca:", termoBusca);
  console.log("üìÇ Categorias:", categorias);
  console.log("üìù Posts:", posts);

  if (!termoBusca) {
    return <MainContainer></MainContainer>;
  }

  const resultados = [...new Set([...categorias, ...posts].map(item => JSON.stringify(item)))]
  .map(str => JSON.parse(str))
  .filter(item => item.nome.toLowerCase().includes(termoBusca.toLowerCase()));


  console.log("üéØ Resultados filtrados:", resultados);
  return (
    <MainContainer>
         <MainContent>
            <h2>teste</h2>
         </MainContent>

        
      {resultados.length === 0 ? (
        <p>Nenhum resultado encontrado.</p>
      ) : (
        resultados.map((item, index) => (
          <Resultado key={`${item.id}-${index}`}>{item.nome}</Resultado>
        ))
      )}
    </MainContainer>
   
  );

}
const MainContainer = styled.section`
  padding: 20px;
`;

const Resultado = styled.div`
  padding: 10px;
  border-bottom: 1px solid #ccc;
`;

const MainContent = styled.section`

`


export default MainPage
