import styled from "styled-components";

const textoOpcoes = ['Home', 'Postagens', 'Categorias', 'Ideias', 'Sobre'];

function NavBar() {
    return (
        <OpcoesContainer> {/* Envolve tudo para centralizar */}
            <Opcoes>
                {textoOpcoes.map((texto) => (
                    <Opcao key={texto}><p>{texto}</p></Opcao>
                ))}
            </Opcoes>
        </OpcoesContainer>
    )
}

const OpcoesContainer = styled.nav`
    display: flex;
    justify-content: center; /* Centraliza a navbar */
    width: 100%;
`;

const Opcoes = styled.ul`
    display: flex;
    gap: 20px; /* Espaçamento entre os itens */
    padding: 15px 20px;
    border: 2px solid black; /* Borda envolvendo tudo */
    border-radius: 8px; /* Bordas arredondadas (opcional) */
    list-style: none; /* Remove os estilos padrão de lista */
    background: white; /* Fundo opcional */
`;

const Opcao = styled.li`
    color: #2d2d34;
    font-size: 18px;
    cursor: pointer;
    text-align: center;
    padding: 5px 10px;

    &:hover {
        color: rgb(101, 101, 116);
    }
`;

export default NavBar;
