import React from "react";
import styled from "styled-components";
import cover from "../../Assets/selfparking-v3.png";
import { XPButton } from '../XPButton';
import xpwindows from '../../Assets/xpwindows.jpg';



const HeadMain = () => {
    return (
        <HeadContainer>
            
            <Content>
                <h1>Olá Terráqueos</h1>
                <p>Explore e compartilhe ideias incríveis!</p>
                <XPButton>Login</XPButton>

            </Content>
            <Image src={cover} alt="Imagem da página principal" />
        </HeadContainer>
    );
}
const HeadContainer = styled.section`
  position: relative;
  display: flex;
  align-items: center;
  justify-content: flex-end;
  flex-wrap: wrap;
  padding: 40px 0;
  width: 100%;
  max-width: 100vw;
  box-sizing: border-box;
  overflow-x: hidden;
  text-align: center;

  background-image: url(${xpwindows});
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;


  &::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.4); 
    z-index: 1;
  }


  & > * {
    position: relative;
    z-index: 2;
  }

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;


const Image = styled.img`
    flex: 1;
   
    max-width: 60%;
    height: auto;
    object-fit: contain;
    display: block;

    @media (max-width: 768px) {
        max-width: 100%;
        width: 100%;
    }
`;
const Content = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: stretch;

  padding: 0;
  color: #fff;
  max-width: 500px;
  background: rgba(128, 0, 128, 0.2);
  backdrop-filter: blur(8px);
  border-radius: 12px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 4px 20px rgba(128, 0, 128, 0.4);
  overflow: hidden;
`;

const TitleBar = styled.div`
  background: purple;
  color: white;
  padding: 8px 12px;
  font-size: 16px;
  font-weight: bold;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Buttons = styled.div`
  display: flex;
  gap: 6px;

  button {
    width: 12px;
    height: 12px;
    border: none;
    border-radius: 50%;
    background-color: white;
    cursor: pointer;

    &:nth-child(1) {
      background-color: #ff5f56; /* fechar */
    }

    &:nth-child(2) {
      background-color: #ffbd2e; /* minimizar */
    }
  }
`;

const InnerContent = styled.div`
  padding: 20px;

  h1 {
    font-size: 48px;
    margin-bottom: 10px;
  }

  p {
    font-size: 22px;
  }
`;




export default HeadMain;
