import React, { useState } from "react";
import styled from "styled-components";
import { useNavigate } from "react-router-dom";
import Categorias from "./Categorias";

function Postagens() {
  
  const [mostrarForm, setMostrarForm] = useState(false);
  const navigate = useNavigate();

  const handleCriarClick = () => {
  setMostrarForm(true);
};


  const MOCK_POSTAGENS = [
    {
      id: 1,
      titulo: "Primeiro post",
      conteudo: "Esse é o conteúdo do post de exemplo.",
      post_imagem: "https://picsum.photos/id/237/200/300",
      categorias_id: "Inspiração",
      usuarios_id: 1,
      moods_id: 2,
    },
    {
      id: 2,
      titulo: "Reflexão do dia",
      conteudo: "Hoje foi um dia muito reflexivo...",
      post_imagem: "https://picsum.photos/id/237/200/300",
      categorias_id: "Reflexões",
      usuarios_id: 2,
      moods_id: 3,
    },
    {
      id: 3,
      titulo: "Gameplay top",
      conteudo: "Review do último jogo que joguei.",
      post_imagem: "https://picsum.photos/id/58/200/300",
      categorias_id: "Games",
      usuarios_id: 3,
      moods_id: 1,
    },
  ];

  

  return (
    <PageWrapper>
      <TituloBlog>📝 Postagens no Blog</TituloBlog>

      <Container>
        <XPWindow>
          <TitleBar>
            <h2>Criar uma nova postagem</h2>
            <StyledButton onClick={handleCriarClick}>Criar Post</StyledButton>
          </TitleBar>
        </XPWindow>

        <Categorias />
      </Container>

      {categoriaSelecionada && (
        <PostsSection>
          <FiltroInfo>
            Posts da categoria: <strong>{categoriaSelecionada}</strong>
          </FiltroInfo>

          <PostsContainer>
            {postsFiltrados.length > 0 ? (
              postsFiltrados.map((post) => (
                <PostCard key={post.id}>
                  <PostImagem src={post.post_imagem} alt={post.titulo} />
                  <PostConteudo>
                    <PostTitulo>{post.titulo}</PostTitulo>
                    <PostTexto>{post.conteudo}</PostTexto>
                    <PostDetalhes>
                      {post.categorias_id} | Por: {post.usuarios_id} | Se sentindo: {post.moods_id}
                    </PostDetalhes>
                  </PostConteudo>
                </PostCard>
              ))
            ) : (
              <SemPosts>Nenhum post nessa categoria.</SemPosts>
            )}
          </PostsContainer>
        </PostsSection>
      )}

      {mostrarForm && (
        <BlurOverlay>
          <PopupWindow>
            <PopupHeader>
              <span>Nova Postagem</span>
              <PopupButtons>
                <button onClick={() => alert("Minimizar...")}>_</button>
                <button onClick={fecharFormulario}>X</button>
              </PopupButtons>
            </PopupHeader>
            <PopupContent>
              <label>Título</label>
              <input type="text" />
              <label>Conteúdo</label>
              <textarea rows="4" />
              <label>Imagem (URL)</label>
              <input type="text" />
              <label>Categoria</label>
              <input type="text" />
              <label>Humor</label>
              <input type="text" />
              <StyledButton onClick={() => alert("Enviar postagem")}>
                Publicar
              </StyledButton>
            </PopupContent>
          </PopupWindow>
        </BlurOverlay>
      )}
    </PageWrapper>
  );
}



const PageWrapper = styled.div`
  background: linear-gradient(135deg, #d4d4ff, #f0e0ff);
  min-height: 100vh;
  padding: 40px 20px;
  font-family: 'Press Start 2P', monospace;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const TituloBlog = styled.h1`
  font-size: 16px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 3px 3px #808080;
  padding: 10px 20px;
  margin-bottom: 30px;
  color: purple;
`;

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: flex-start;
  gap: 20px;
  flex-wrap: wrap;
`;

const XPWindow = styled.div`
  max-width: 600px;
  border: 2px solid #000;
  background-color: #e0e0e0;
  box-shadow: inset -2px -2px #fff, inset 2px 2px #808080, 3px 3px #999;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const TitleBar = styled.div`
  background-color: purple;
  color: white;
  padding: 20px;
  font-size: 20px;
  border-bottom: 2px solid #000;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 20px;

  h2 {
    margin: 0;
    font-size: 14px;
    text-align: center;
  }
`;

const StyledButton = styled.button`
  font-family: 'Press Start 2P', monospace;
  font-size: 12px;
  padding: 10px 20px;
  background-color: #fff;
  color: purple;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  cursor: pointer;

  &:hover {
    background-color: #f0f0f0;
  }
`;

// Postagens
const PostsSection = styled.section`
  margin-top: 40px;
  max-width: 700px;
  width: 100%;
  font-family: 'Press Start 2P', monospace;
`;

const FiltroInfo = styled.div`
  font-size: 12px;
  margin-bottom: 15px;
  color: purple;
  font-weight: bold;
`;

const PostsContainer = styled.div`
  margin-top: 10px;
  width: 100%;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const PostCard = styled.div`
  display: flex;
  gap: 15px;
  background-color: #fff;
  border: 2px solid #000;
  box-shadow: 2px 2px #808080;
  padding: 15px;
  border-radius: 6px;
`;

const PostImagem = styled.img`
  width: 120px;
  height: 120px;
  object-fit: cover;
  border: 2px solid #000;
  border-radius: 6px;
`;

const PostConteudo = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
`;

const PostTitulo = styled.h3`
  margin: 0;
  font-size: 14px;
  color: purple;
`;

const PostTexto = styled.p`
  margin: 8px 0 12px 0;
  font-size: 10px;
  color: #222;
`;

const PostDetalhes = styled.div`
  font-size: 8px;
  color: #666;
`;

const SemPosts = styled.div`
  font-size: 12px;
  color: purple;
  font-weight: bold;
  text-align: center;
  margin-top: 20px;
`;


const BlurOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  backdrop-filter: blur(5px);
  background-color: rgba(0, 0, 0, 0.2);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 99;
`;

const PopupWindow = styled.div`
  width: 400px;
  background-color: #e0e0e0;
  border: 2px solid #000;
  box-shadow: 4px 4px #555;
  padding: 0;
  font-family: 'Press Start 2P', monospace;
`;

const PopupHeader = styled.div`
  background-color: purple;
  color: white;
